  // Preview handlers (event delegation)
  initPreviewHandlers();

  // Helper to safely init modules
  const safeInit = (name, fn) => {
    try {
      if (typeof fn === 'function') {
        fn();
      } else {
        console.warn(`[SPLICE] ${name} function not found`);
      }
    } catch (err) {
