     1→<!DOCTYPE html>
     2→<html>
     3→<head>
     4→  <meta charset="utf-8">
     5→  <title>SPLICE</title>
     6→  <style>
     7→    body {
     8→      font-family: Adobe Clean, -apple-system, BlinkMacSystemFont, sans-serif;
     9→      background: #323232;
    10→      color: #e1e1e1;
    11→      margin: 0;
    12→      padding: 8px;
    13→      height: 100vh;
    14→      box-sizing: border-box;
    15→      overflow-y: auto;
    16→      overflow-x: hidden;
    17→    }
    18→
    19→    /* Header */
    20→    .header {
    21→      display: flex;
    22→      justify-content: space-between;
    23→      align-items: center;
    24→      margin-bottom: 8px;
    25→    }
    26→    .header h1 {
    27→      font-size: 14px;
    28→      font-weight: 600;
    29→      margin: 0;
    30→    }
    31→    .header-actions {
    32→      display: flex;
    33→      gap: 4px;
    34→    }
    35→    .icon-btn {
    36→      background: transparent;
    37→      border: 1px solid #555;
    38→      color: #aaa;
    39→      width: 24px;
    40→      height: 24px;
    41→      border-radius: 4px;
    42→      cursor: pointer;
    43→      font-size: 12px;
    44→      display: flex;
    45→      align-items: center;
    46→      justify-content: center;
    47→      padding: 0;
    48→      margin: 0;
    49→    }
    50→    .icon-btn:hover {
    51→      background: #444;
    52→      color: #fff;
    53→    }
    54→
    55→    /* Credit Badge */
    56→    .credit-badge {
    57→      display: none;
    58→      align-items: center;
    59→      font-size: 10px;
    60→      font-weight: 500;
    61→      padding: 3px 8px;
    62→      border-radius: 10px;
    63→      background: #1e1e1e;
    64→      border: 1px solid #444;
    65→      color: #aaa;
    66→      margin-right: 8px;
    67→    }
    68→    .credit-badge.ok {
    69→      color: #4a9eff;
    70→      border-color: #4a9eff33;
    71→    }
    72→    .credit-badge.low {
    73→      color: #dc3545;
    74→      border-color: #dc354533;
    75→      animation: pulse-low 2s infinite;
    76→    }
    77→    @keyframes pulse-low {
    78→      0%, 100% { opacity: 1; }
    79→      50% { opacity: 0.6; }
    80→    }
    81→
    82→    /* Status bar */
    83→    #status {
    84→      padding: 6px 10px;
    85→      background: #1e1e1e;
    86→      border-radius: 4px;
    87→      font-size: 11px;
    88→      margin-bottom: 10px;
    89→    }
    90→
    91→    /* Action Cards Container */
    92→    .action-cards {
    93→      display: flex;
    94→      gap: 8px;
    95→      margin-bottom: 10px;
    96→    }
    97→
    98→    /* Action Card */
    99→    .action-card {
   100→      flex: 1;
   101→      background: #2a2a2a;
   102→      border-radius: 6px;
   103→      padding: 12px;
   104→      border: 1px solid #444;
   105→    }
   106→    .action-card h2 {
   107→      font-size: 11px;
   108→      font-weight: 600;
   109→      margin: 0 0 10px 0;
   110→      color: #ccc;
   111→      text-transform: uppercase;
   112→      letter-spacing: 0.5px;
   113→    }
   114→
   115→    /* GO Button */
   116→    .go-btn {
   117→      width: 100%;
   118→      padding: 14px 12px;
   119→      font-size: 13px;
   120→      font-weight: 700;
   121→      background: linear-gradient(135deg, #4a9eff, #2d7dd2);
   122→      border: none;
   123→      border-radius: 6px;
   124→      color: white;
   125→      cursor: pointer;
   126→      margin-bottom: 8px;
   127→      letter-spacing: 1px;
   128→    }
   129→    .go-btn:hover {
   130→      background: linear-gradient(135deg, #5aa8ff, #3d8de2);
   131→    }
   132→    .go-btn:disabled {
   133→      opacity: 0.5;
   134→      cursor: not-allowed;
   135→    }
   136→    .go-btn.takes-btn {
   137→      background: linear-gradient(135deg, #28a745, #1e7e34);
   138→    }
   139→    .go-btn.takes-btn:hover {
   140→      background: linear-gradient(135deg, #34ce57, #28a745);
   141→    }
   142→
   143→    /* Options Toggle */
   144→    .options-toggle {
   145→      background: transparent;
   146→      border: none;
   147→      color: #888;
   148→      font-size: 10px;
   149→      cursor: pointer;
   150→      padding: 4px 0;
   151→      width: 100%;
   152→      text-align: left;
   153→      margin: 0;
   154→    }
   155→    .options-toggle:hover {
   156→      color: #aaa;
   157→    }
   158→    .options-toggle::before {
   159→      content: '+';
   160→      margin-right: 4px;
   161→      font-weight: bold;
   162→    }
   163→    .options-toggle.expanded::before {
   164→      content: '-';
   165→    }
   166→
   167→    /* Options Panel (collapsible) */
   168→    .options-panel {
   169→      margin-top: 8px;
   170→      padding-top: 8px;
   171→      border-top: 1px solid #444;
   172→    }
   173→    .options-panel.collapsed {
   174→      display: none;
   175→    }
   176→
   177→    /* Slider */
   178→    .slider-container {
   179→      margin-bottom: 10px;
   180→    }
   181→    .slider-container sp-slider {
   182→      width: 100%;
   183→    }
   184→    .slider-labels {
   185→      display: flex;
   186→      justify-content: space-between;
   187→      font-size: 9px;
   188→      color: #666;
   189→      margin-top: 2px;
   190→    }
   191→
   192→    /* Audio Source */
   193→    .audio-source-group {
   194→      margin-bottom: 8px;
   195→    }
   196→    .audio-source-group sp-label {
   197→      display: block;
   198→      font-size: 10px;
   199→      color: #888;
   200→      margin-bottom: 4px;
   201→    }
   202→    .audio-source-group sp-checkbox {
   203→      display: block;
   204→      margin: 4px 0;
   205→      font-size: 11px;
   206→    }
   207→    .tier-badge {
   208→      font-size: 8px;
   209→      background: #4a9eff;
   210→      color: white;
   211→      padding: 1px 4px;
   212→      border-radius: 2px;
   213→      margin-left: 4px;
   214→      vertical-align: middle;
   215→    }
   216→
   217→    /* Results Area */
   218→    .results-area {
   219→      background: #1e1e1e;
   220→      border-radius: 6px;
   221→      padding: 12px;
   222→      min-height: 60px;
   223→    }
   224→    .results-area.hidden {
   225→      display: none;
   226→    }
   227→    .results-header {
   228→      display: flex;
   229→      justify-content: space-between;
   230→      align-items: center;
   231→      margin-bottom: 8px;
   232→    }
   233→    .results-header h3 {
   234→      font-size: 11px;
   235→      font-weight: 600;
   236→      margin: 0;
   237→      color: #aaa;
   238→      text-transform: uppercase;
   239→    }
   240→    .undo-btn {
   241→      background: #dc3545;
   242→      color: white;
   243→      border: none;
   244→      padding: 4px 10px;
   245→      border-radius: 3px;
   246→      font-size: 10px;
   247→      cursor: pointer;
   248→    }
   249→    .undo-btn:hover {
   250→      background: #c82333;
   251→    }
   252→
   253→    /* Progress */
   254→    .progress-container {
   255→      text-align: center;
   256→      padding: 16px 0;
   257→    }
   258→    .progress-container sp-progressbar {
   259→      width: 100%;
   260→      margin-bottom: 8px;
   261→    }
   262→    .progress-text {
   263→      font-size: 11px;
   264→      color: #888;
   265→    }
   266→
   267→    /* Results Content */
   268→    .results-content {
   269→      font-size: 11px;
   270→    }
   271→    .results-stat {
   272→      display: flex;
   273→      justify-content: space-between;
   274→      padding: 4px 0;
   275→      border-bottom: 1px solid #333;
   276→    }
   277→    .results-stat:last-child {
   278→      border-bottom: none;
   279→    }
   280→    .results-stat-label {
   281→      color: #888;
   282→    }
   283→    .results-stat-value {
   284→      font-weight: 600;
   285→      color: #4a9eff;
   286→    }
   287→
   288→    /* Preview Section */
   289→    .preview-section {
   290→      display: none;
   291→    }
   292→    .preview-header {
   293→      display: flex;
   294→      justify-content: space-between;
   295→      align-items: center;
   296→      margin-bottom: 10px;
   297→    }
   298→    .preview-header h3 {
   299→      font-size: 11px;
   300→      font-weight: 600;
   301→      margin: 0;
   302→      color: #aaa;
   303→      text-transform: uppercase;
   304→    }
   305→    .preview-header-actions {
   306→      display: flex;
   307→      gap: 4px;
   308→    }
   309→    .preview-summary {
   310→      font-size: 10px;
   311→      color: #888;
   312→      margin-bottom: 10px;
   313→      padding: 8px;
   314→      background: #252525;
   315→      border-radius: 4px;
   316→    }
   317→    .preview-summary strong {
   318→      color: #4a9eff;
   319→    }
   320→    .preview-list {
   321→      max-height: 180px;
   322→      overflow-y: auto;
   323→      margin-bottom: 10px;
   324→      contain: layout style paint;
   325→      will-change: scroll-position;
   326→    }
   327→    .preview-item {
   328→      display: flex;
   329→      align-items: center;
   330→      padding: 8px 10px;
   331→      min-height: 44px;
   332→      margin-bottom: 4px;
   333→      background: #2a2a2a;
   334→      border-radius: 4px;
   335→      border-left: 3px solid #4a9eff;
   336→      cursor: pointer;
   337→      transition: background-color 0.15s ease, border-left-color 0.15s ease;
   338→      contain: layout style;
   339→      box-sizing: border-box;
   340→    }
   341→    .preview-item:hover {
   342→      background: #333;
   343→    }
   344→    .preview-item:focus-within {
   345→      outline: 2px solid #4a9eff;
   346→      outline-offset: -2px;
   347→    }
   348→    .preview-item.excluded {
   349→      opacity: 0.65;
   350→      border-left-color: #666;
   351→    }
   352→    .preview-item-check {
   353→      min-width: 20px;
   354→      min-height: 20px;
   355→      margin-right: 10px;
   356→      accent-color: #4a9eff;
   357→      cursor: pointer;
   358→    }
   359→    .preview-item-info {
   360→      flex: 1;
   361→      min-width: 0;
   362→      overflow: hidden;
   363→    }
   364→    .preview-item-time {
   365→      font-size: 11px;
   366→      font-weight: 500;
   367→      color: #e1e1e1;
   368→      white-space: nowrap;
   369→      overflow: hidden;
   370→      text-overflow: ellipsis;
   371→    }
   372→    .preview-item-duration {
   373→      font-size: 9px;
   374→      color: #999;
   375→      margin-top: 2px;
   376→    }
   377→    .preview-item-seek {
   378→      background: transparent;
   379→      border: 1px solid #555;
   380→      color: #888;
   381→      min-width: 32px;
   382→      min-height: 32px;
   383→      border-radius: 4px;
   384→      cursor: pointer;
   385→      font-size: 12px;
   386→      display: flex;
   387→      align-items: center;
   388→      justify-content: center;
   389→      flex-shrink: 0;
   390→    }
   391→    .preview-item-seek:hover {
   392→      background: #444;
   393→      color: #fff;
   394→    }
   395→    .preview-item-seek:focus-visible {
   396→      outline: 2px solid #4a9eff;
   397→      outline-offset: 2px;
   398→    }
   399→    .preview-item-seek.loading {
   400→      pointer-events: none;
   401→      opacity: 0.6;
   402→    }
   403→    .preview-actions {
   404→      display: flex;
   405→      gap: 8px;
   406→      margin-top: 10px;
   407→    }
   408→    .preview-actions button {
   409→      flex: 1;
   410→      padding: 12px 10px;
   411→      min-height: 44px;
   412→      font-size: 11px;
   413→      font-weight: 600;
   414→      border-radius: 4px;
   415→      cursor: pointer;
   416→    }
   417→    .apply-btn {
   418→      background: linear-gradient(135deg, #28a745, #1e7e34);
   419→      color: white;
   420→      border: none;
   421→    }
   422→    .apply-btn:hover {
   423→      background: linear-gradient(135deg, #34ce57, #28a745);
   424→    }
   425→    .apply-btn:disabled {
   426→      opacity: 0.5;
   427→      cursor: not-allowed;
   428→    }
   429→    .apply-btn:focus-visible {
   430→      outline: 2px solid #28a745;
   431→      outline-offset: 2px;
   432→    }
   433→    .cancel-btn {
   434→      background: transparent;
   435→      color: #888;
   436→      border: 1px solid #555;
   437→    }
   438→    .cancel-btn:hover {
   439→      background: #333;
   440→      color: #e1e1e1;
   441→    }
   442→    .cancel-btn:focus-visible {
   443→      outline: 2px solid #888;
   444→      outline-offset: 2px;
   445→    }
   446→    .select-all-row {
   447→      display: flex;
   448→      justify-content: space-between;
   449→      align-items: center;
   450→      padding: 6px 0;
   451→      margin-bottom: 8px;
   452→      border-bottom: 1px solid #333;
   453→    }
   454→    .select-all-row label {
   455→      font-size: 10px;
   456→      color: #888;
   457→      display: flex;
   458→      align-items: center;
   459→      cursor: pointer;
   460→    }
   461→    .select-all-row input {
   462→      margin-right: 6px;
   463→      min-width: 16px;
   464→      min-height: 16px;
   465→      accent-color: #4a9eff;
   466→    }
   467→    .preview-filters {
   468→      display: flex;
   469→      gap: 4px;
   470→      flex-wrap: wrap;
   471→      margin-bottom: 8px;
   472→    }
   473→    .filter-btn {
   474→      background: #1e1e1e;
   475→      border: 1px solid #444;
   476→      color: #888;
   477→      padding: 4px 8px;
   478→      font-size: 9px;
   479→      border-radius: 3px;
   480→      cursor: pointer;
   481→    }
   482→    .filter-btn:hover {
   483→      background: #333;
   484→      color: #e1e1e1;
   485→    }
   486→    .filter-btn.active {
   487→      background: #4a9eff22;
   488→      border-color: #4a9eff;
   489→      color: #4a9eff;
   490→    }
   491→
   492→    /* Legend dots */
   493→    .legend-item {
   494→      display: inline-flex;
   495→      align-items: center;
   496→      margin-right: 12px;
   497→    }
   498→    .legend-dot {
   499→      display: inline-block;
   500→      width: 8px;
   501→      height: 8px;
   502→      border-radius: 50%;
   503→      margin-right: 4px;
   504→    }
   505→    .legend-dot.silence {
   506→      background: #dc3545;
   507→    }
   508→    .legend-dot.take {
   509→      background: #4a9eff;
   510→    }
   511→
   512→    /* Colored markers for preview items */
   513→    .preview-item.silence-marker {
   514→      border-left-color: #dc3545;
   515→    }
   516→    .preview-item.take-marker {
   517→      border-left-color: #4a9eff;
   518→    }
   519→    .preview-item.protected-silence {
   520→      border-left-color: #ffc107;
   521→      opacity: 0.6;
   522→    }
   523→    .preview-item.protected-silence::after {
   524→      content: 'Protected';
   525→      font-size: 8px;
   526→      color: #ffc107;
   527→      margin-left: 8px;
   528→    }
   529→
   530→    /* Take Items */
   531→    .takes-list {
   532→      max-height: 200px;
   533→      overflow-y: auto;
   534→    }
   535→    .take-item {
   536→      padding: 8px;
   537→      margin-bottom: 4px;
   538→      background: #2a2a2a;
   539→      border-radius: 4px;
   540→      border-left: 3px solid #555;
   541→      cursor: pointer;
   542→      transition: all 0.15s ease;
   543→    }
   544→    .take-item:hover {
   545→      background: #333;
   546→    }
   547→    .take-item.best {
   548→      border-left-color: #28a745;
   549→      background: #1a2e1a;
   550→    }
   551→    .take-header {
   552→      display: flex;
   553→      justify-content: space-between;
   554→      align-items: center;
   555→    }
   556→    .take-label {
   557→      font-weight: 500;
   558→      font-size: 11px;
   559→    }
   560→    .take-star {
   561→      font-size: 14px;
   562→      color: #555;
   563→    }
   564→    .take-item.best .take-star {
   565→      color: #28a745;
   566→    }
   567→    .take-time {
   568→      color: #888;
   569→      font-size: 9px;
   570→      margin-top: 2px;
   571→    }
   572→    .take-text {
   573→      color: #aaa;
   574→      font-size: 10px;
   575→      margin-top: 4px;
   576→    }
   577→    .take-actions {
   578→      display: flex;
   579→      gap: 6px;
   580→      margin-top: 10px;
   581→    }
   582→    .take-actions button {
   583→      flex: 1;
   584→      padding: 8px;
   585→      font-size: 10px;
   586→      border-radius: 4px;
   587→    }
   588→    .keep-best-btn {
   589→      background: #28a745;
   590→      color: white;
   591→      border: none;
   592→      cursor: pointer;
   593→    }
   594→    .keep-best-btn:hover {
   595→      background: #218838;
   596→    }
   597→    .label-all-btn {
   598→      background: #6c757d;
   599→      color: white;
   600→      border: none;
   601→      cursor: pointer;
   602→    }
   603→    .label-all-btn:hover {
   604→      background: #5a6268;
   605→    }
   606→
   607→    /* Empty State */
   608→    .empty-state {
   609→      text-align: center;
   610→      color: #666;
   611→      font-size: 11px;
   612→      padding: 20px 0;
   613→    }
   614→
   615→    /* Razor Section (Advanced - hidden by default) */
   616→    .advanced-section {
   617→      margin-top: 10px;
   618→      padding: 10px;
   619→      background: #252525;
   620→      border-radius: 4px;
   621→      border: 1px dashed #444;
   622→    }
   623→    .advanced-section.hidden {
   624→      display: none;
   625→    }
   626→    .advanced-section h4 {
   627→      font-size: 10px;
   628→      color: #888;
   629→      margin: 0 0 8px 0;
   630→      text-transform: uppercase;
   631→    }
   632→    .advanced-section p {
   633→      font-size: 9px;
   634→      color: #666;
   635→      margin: 0 0 8px 0;
   636→    }
   637→    .advanced-section button {
   638→      font-size: 10px;
   639→      padding: 6px 10px;
   640→      margin-right: 6px;
   641→    }
   642→    button.razor-btn {
   643→      background: #6f42c1;
   644→      color: white;
   645→      border: none;
   646→      border-radius: 3px;
   647→      cursor: pointer;
   648→    }
   649→    button.razor-btn:hover {
   650→      background: #5a32a3;
   651→    }
   652→    button.export-xml-btn {
   653→      background: #17a2b8;
   654→      color: white;
   655→      border: none;
   656→      border-radius: 3px;
   657→      cursor: pointer;
   658→    }
   659→    button.export-xml-btn:hover {
   660→      background: #138496;
   661→    }
   662→
   663→    /* Settings Modal */
   664→    .modal {
   665→      position: fixed;
   666→      top: 0;
   667→      left: 0;
   668→      right: 0;
   669→      bottom: 0;
   670→      background: rgba(0,0,0,0.7);
   671→      display: flex;
   672→      align-items: center;
   673→      justify-content: center;
   674→      z-index: 100;
   675→    }
   676→    .modal.hidden {
   677→      display: none;
   678→    }
   679→    .modal-content {
   680→      background: #2a2a2a;
   681→      border-radius: 8px;
   682→      padding: 16px;
   683→      width: 90%;
   684→      max-width: 280px;
   685→      border: 1px solid #444;
   686→    }
   687→    .modal-header {
   688→      display: flex;
   689→      justify-content: space-between;
   690→      align-items: center;
   691→      margin-bottom: 12px;
   692→    }
   693→    .modal-header h3 {
   694→      font-size: 12px;
   695→      margin: 0;
   696→    }
   697→    .modal-close {
   698→      background: transparent;
   699→      border: none;
   700→      color: #888;
   701→      font-size: 18px;
   702→      cursor: pointer;
   703→      padding: 0;
   704→      width: 24px;
   705→      height: 24px;
   706→    }
   707→    .modal-close:hover {
   708→      color: #fff;
   709→    }
   710→    .setting-group {
   711→      margin-bottom: 12px;
   712→    }
   713→    .setting-group label {
   714→      display: block;
