   305→
   306→  } catch (err) {
   307→    console.error('[SPLICE Builder] Build error:', err);
   308→    return { success: false, error: err.message, stats };
   309→  }
   310→}
   311→
   312→/**
   313→ * Get color index for a segment type
   314→ * @param {string} type - Segment type (speech, silence, best_take)
   315→ * @returns {number|null} Color label index or null for no color
   316→ */
   317→function getColorForSegmentType(type) {
   318→  switch (type) {
   319→    case 'speech':
   320→    case 'take':
   321→      return SPLICE_COLORS.SPEECH;
   322→    case 'best_take':
   323→      return SPLICE_COLORS.BEST_TAKE;
   324→    case 'silence':
   325→      return SPLICE_COLORS.SILENCE;
