   331→/**
   332→ * Clear all clips from a sequence
   333→ * @param {Object} sequence - The sequence to clear
   334→ */
   335→async function clearSequence(sequence) {
   336→  try {
   337→    const project = await pproBuilder.Project.getActiveProject();
   338→
   339→    // Get all video tracks
   340→    const videoTrackCount = await sequence.getVideoTrackCount();
   341→    const audioTrackCount = await sequence.getAudioTrackCount();
   342→
   343→    const itemsToDelete = [];
   344→
   345→    // Collect all video track items
   346→    for (let i = 0; i < videoTrackCount; i++) {
