   351→          itemsToDelete.push(...items);
   352→        }
   353→      }
   354→    }
   355→
   356→    // Collect all audio track items
   357→    for (let i = 0; i < audioTrackCount; i++) {
   358→      const track = await sequence.getAudioTrack(i);
   359→      if (track) {
   360→        const items = await track.getTrackItems(pproBuilder.Constants.TrackItemType.CLIP, false);
   361→        if (items) {
   362→          itemsToDelete.push(...items);
   363→        }
   364→      }
   365→    }
   366→
   367→    // Delete all items in one transaction
   368→    if (itemsToDelete.length > 0) {
   369→      try {
   370→        await project.lockedAccess(async () => {
   371→          await project.executeTransaction((compoundAction) => {
   372→            for (const item of itemsToDelete) {
   373→              const deleteAction = item.createRemoveAction();
   374→              compoundAction.addAction(deleteAction);
   375→            }
   376→          }, 'SPLICE: Clear Sequence');
   377→        });
   378→      } catch (clearErr) {
   379→        console.error('[SPLICE Builder] Failed to clear sequence:', clearErr);
   380→        // Non-fatal - continue with insert
   381→      }
