   388→/**
   389→ * Set in/out points for track items after insertion
   390→ * @param {Object} sequence - The sequence containing the clips
   391→ * @param {Array} segmentsToInsert - The segment data with in/out points
   392→ */
   393→async function setTrackItemInOutPoints(sequence, segmentsToInsert) {
   394→  try {
   395→    // Get video track
   396→    const videoTrack = await sequence.getVideoTrack(0);
   397→    if (!videoTrack) return;
   398→
   399→    const trackItems = await videoTrack.getTrackItems(pproBuilder.Constants.TrackItemType.CLIP, false);
   400→    if (!trackItems || trackItems.length === 0) return;
   401→
   402→    const project = await pproBuilder.Project.getActiveProject();
   403→
   404→    // Match track items with segments (in order)
   405→    const itemCount = Math.min(trackItems.length, segmentsToInsert.length);
   406→
   407→    if (itemCount > 0) {
   408→      try {
   409→        await project.lockedAccess(async () => {
   410→          await project.executeTransaction((compoundAction) => {
   411→            for (let i = 0; i < itemCount; i++) {
   412→              const trackItem = trackItems[i];
   413→              const segData = segmentsToInsert[i];
   414→
   415→              // Use the pre-created TickTime objects (no conversion needed)
   416→              const inPointTime = segData.inPoint;
   417→              const outPointTime = segData.outPoint;
   418→
   419→              // Create actions to set in/out points
   420→              const inAction = trackItem.createSetInPointAction(inPointTime);
   421→              const outAction = trackItem.createSetOutPointAction(outPointTime);
   422→
   423→              compoundAction.addAction(inAction);
   424→              compoundAction.addAction(outAction);
   425→            }
   426→          }, 'SPLICE: Set In/Out Points');
   427→        });
   428→      } catch (inOutErr) {
   429→        console.error('[SPLICE Builder] Failed to set in/out points:', inOutErr);
   430→        // Non-fatal - clips are inserted but may have wrong in/out points
   431→      }
   432→    }
   433→
   434→  } catch (err) {
   435→    console.error('[SPLICE Builder] Error setting in/out points:', err);
   436→  }
   437→}
