   458→  const durationObj = await sourceClip.getDuration();
   459→  const durationSecs = durationObj ? durationObj.seconds : 0;
   460→
   461→  // Sort silences by start time
   462→  const sortedSilences = [...silences].sort((a, b) => a.start - b.start);
   463→
   464→  // Build segments from gaps between silences (speech segments)
   465→  let lastEnd = 0;
