   471→        type: 'speech',
   472→        sourceName,
   473→        sourcePath,
   474→        inPoint: lastEnd,
   475→        outPoint: silence.start
   476→      });
   477→    }
   478→    lastEnd = silence.end;
   479→  }
   480→
   481→  // Add final segment after last silence
   482→  if (lastEnd < durationSecs) {
   483→    segments.push({
   484→      type: 'speech',
   485→      sourceName,
   486→      sourcePath,
   487→      inPoint: lastEnd,
   488→      outPoint: durationSecs
   489→    });
   490→  }
   491→
   492→  // Build the cut list
   493→  const cutList = {
   494→    version: '3.5',
   495→    source: sourceName,
   496→    segments,
   497→    metadata: {
   498→      silencesRemoved: silences.length,
   499→      takesDetected: takes?.length || 0,
   500→      generatedAt: new Date().toISOString()
   501→    }
   502→  };
   503→
   504→  return await buildSequenceFromCutList(cutList);
   505→}
