  1173→  if (item.children && item.children.numItems > 0) {
  1174→    for (let i = 0; i < item.children.numItems; i++) {
  1175→      const child = item.children[i];
  1176→      const found = await findFirstMediaItem(child);
  1177→      if (found) return found;
