    buildSequenceFromCutList,
    processCutList,

    // Markers
    addSilenceMarkers,
    addTakeMarkers,
    addChapterMarkers,
    addZoomMarkers,
    clearSpliceMarkers,

    // Razor
    razorAtSilences,
    deleteClipsAtSilences,

    // Utilities
    getColorForSegment,
    getActiveSequence,
    checkSequenceOpen,
    cloneSequence,
    undo
};

