  1137→            var sourceItem = null;
  1138→            if (segment.sourcePath) {
  1139→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1140→            }
  1141→            if (!sourceItem && segment.sourceName) {
  1142→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1143→            }
  1144→
  1145→            if (!sourceItem) continue;
  1146→
  1147→            // Set color label
  1148→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1149→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1150→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1151→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1152→            }
  1153→
  1154→            // Insert clip at current position
  1155→            var targetTime = new Time();
  1156→            targetTime.seconds = currentPosition;
  1157→
  1158→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1159→
  1160→            // Set in/out points
  1161→            var clipDuration = segment.outPoint - segment.inPoint;
  1162→            var videoClips = newSeq.videoTracks[0].clips;
  1163→            var insertedClip = videoClips[videoClips.numItems - 1];
  1164→
  1165→            if (insertedClip) {
  1166→                var inTime = new Time();
  1167→                inTime.seconds = segment.inPoint;
  1168→                insertedClip.inPoint = inTime;
  1169→
  1170→                var outTime = new Time();
  1171→                outTime.seconds = segment.outPoint;
  1172→                insertedClip.outPoint = outTime;
  1173→
  1174→                // Set clip name if takeLabel provided
  1175→                if (segment.takeLabel) {
  1176→                    insertedClip.name = segment.takeLabel;
  1177→                }
  1178→            }
  1179→
  1180→            currentPosition += clipDuration;
  1181→            stats.segmentsInserted++;
  1182→            stats.totalDuration += clipDuration;
  1183→        }
  1184→
  1185→        return JSON.stringify({
  1186→            success: true,
  1187→            sequenceName: newSequenceName,
  1188→            stats: stats
  1189→        });
  1190→    } catch (e) {
  1191→        return JSON.stringify({ error: e.message });
  1192→    }
  1193→}
