  1410→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1411→    if (!isQEAvailable() && !enableQE()) {
  1412→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1413→    }
  1414→
  1415→    try {
  1416→        var accuracy = 3;
  1417→        var search_start_time = clipStartTime.toFixed(accuracy);
  1418→
  1419→        if (trackType == "audio") {
