  1421→            for (var j = 0; j < numClipsInTrack; j++) {
  1422→                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1423→                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1424→                    return JSON.stringify({ success: true });
  1425→                }
  1426→            }
  1427→        } else if (trackType == "video") {
  1428→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1429→            for (var j = 0; j < numClipsInTrack; j++) {
  1430→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1431→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1432→                    return JSON.stringify({ success: true });
  1433→                }
  1434→            }
  1435→        }
  1436→        return JSON.stringify({ error: "Clip not found at specified time" });
