   532→function applyPreset(presetId) {
   533→    const preset = PRESETS[presetId];
   534→    if (!preset) return;
   535→
   536→    // Custom preset - keep current settings
   537→    if (presetId === 'custom' || !preset.settings) {
   538→        saveSettings({ activePreset: 'custom' });
   539→        setStatus('Using custom settings');
   540→        return;
   541→    }
   542→
   543→    const settings = preset.settings;
   544→
   545→    // Apply to UI
   546→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   547→        ui.sensitivitySlider.value = settings.sensitivity;
   548→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   549→    }
   550→
   551→    if (ui.enableJCut) {
   552→        ui.enableJCut.checked = settings.enableJCut || false;
   553→        if (ui.jcutSettings) {
   554→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   555→        }
   556→    }
   557→
   558→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   559→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   560→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   561→    }
   562→
   563→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   564→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   565→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   566→    }
   567→
   568→    // Save merged settings
   569→    saveSettings({
   570→        activePreset: presetId,
   571→        sensitivity: settings.sensitivity,
   572→        threshold: settings.threshold,
   573→        minSilenceLength: settings.minSilenceLength,
   574→        paddingStart: settings.paddingStart,
   575→        paddingEnd: settings.paddingEnd,
   576→        enableTakesDetection: settings.enableTakesDetection,
   577→        enableJCut: settings.enableJCut || false,
   578→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   579→        jcutLeadOut: settings.jCutLeadOut || 0.2
   580→    });
   581→
   582→    setStatus(`Applied ${preset.name} preset`);
   583→}
