   630→
   631→async function runDetection() {
   632→    if (isOperationInProgress) return;
   633→
   634→    // Check sequence with enhanced error messaging
   635→    const hasSequence = await checkSequenceOpen();
   636→    if (!hasSequence) {
   637→        // Check if there was a communication error vs just no sequence
   638→        const lastError = window.spliceState.lastSequenceCheckError;
   639→        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   640→            setStatus('Premiere Pro connection issue - try reopening panel', true);
   641→            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   642→        } else {
   643→            setStatus('Please open a sequence in the Timeline first', true);
   644→            debugLog('runDetection: No active sequence in Timeline');
   645→        }
   646→        // Clear the error for next check
   647→        window.spliceState.lastSequenceCheckError = null;
   648→        return;
   649→    }
   650→
   651→    // Check online
   652→    if (!isOnline()) {
   653→        setStatus('Offline - Check your connection', true);
   654→        return;
   655→    }
   656→
   657→    isOperationInProgress = true;
   658→    ui.goBtn.disabled = true;
   659→    showProgress('Detecting silences...');
   660→
   661→    try {
   662→        const settings = getSettings();
   663→
   664→        // Get sequence info
   665→        const seqInfo = await jsx.call('getActiveSequence');
   666→        debugLog('Sequence info:', seqInfo);
   667→
   668→        // Store sequence info for later use
   669→        currentSequenceInfo = seqInfo;
   670→        window.spliceState.activeSequence = seqInfo;
   671→        if (seqInfo && seqInfo.videoPath) {
   672→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   673→        }
   674→
   675→        // Export audio via JSX for silence detection
   676→        updateProgress(10, 'Exporting audio...');
   677→
   678→        let audioFilePath = null;
   679→        try {
   680→            // Get sequence info for audio export
   681→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   682→            if (exportResult && exportResult.success && exportResult.outputPath) {
   683→                audioFilePath = exportResult.outputPath;
   684→                debugLog('Audio exported to:', audioFilePath);
   685→            } else if (exportResult && exportResult.error) {
   686→                throw new Error(exportResult.error);
   687→            }
   688→        } catch (exportError) {
   689→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   690→            // Try to get audio from first clip in timeline
   691→            try {
   692→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   693→                if (clipInfo && clipInfo.path) {
   694→                    audioFilePath = clipInfo.path;
   695→                    debugLog('Using clip audio path:', audioFilePath);
   696→                }
   697→            } catch (clipError) {
   698→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   699→            }
   700→        }
   701→
   702→        if (!audioFilePath) {
   703→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   704→        }
   705→
   706→        // Store audio path globally for other modules
   707→        currentAudioPath = audioFilePath;
   708→        window.spliceState.audioPath = audioFilePath;
   709→
   710→        // Call backend for silence detection with audio file path
   711→        updateProgress(30, 'Detecting silences...');
   712→        const silenceResponse = await fetchWithTimeout(
   713→            `${getBackendUrl()}/silences-rms`,
   714→            {
   715→                method: 'POST',
   716→                headers: getAuthHeaders(),
   717→                body: JSON.stringify({
   718→                    wavPath: audioFilePath,
   719→                    sensitivity: settings.sensitivity,
   720→                    minSilenceLength: settings.minSilenceLength || 0.5
   721→                })
   722→            }
   723→        );
   724→
   725→        if (!silenceResponse.ok) {
   726→            throw new Error(await parseErrorResponse(silenceResponse));
   727→        }
   728→
   729→        const silenceData = await silenceResponse.json();
   730→        previewSilences = silenceData.silences || [];
   731→
   732→        // Detect takes if enabled
   733→        if (settings.enableTakesDetection) {
   734→            updateProgress(60, 'Detecting takes...');
   735→            try {
   736→                const analyzeResponse = await fetchWithTimeout(
   737→                    `${getBackendUrl()}/analyze`,
   738→                    {
   739→                        method: 'POST',
   740→                        headers: getAuthHeaders(),
   741→                        body: JSON.stringify({
   742→                            wavPath: audioFilePath,
   743→                            detectTakes: true
   744→                        })
   745→                    }
   746→                );
   747→
   748→                if (analyzeResponse.ok) {
   749→                    const analyzeData = await analyzeResponse.json();
   750→                    previewTakes = analyzeData.takes || [];
   751→
   752→                    // Store transcript for captions/music modules
   753→                    if (analyzeData.transcript) {
   754→                        currentTranscript = analyzeData.transcript;
   755→                        window.currentTranscript = analyzeData.transcript;
   756→                        window.spliceState.lastTranscript = analyzeData.transcript;
   757→                    }
   758→                } else {
   759→                    // Handle specific error codes
   760→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   761→                    if (analyzeResponse.status === 402) {
   762→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   763→                        setStatus('Takes detection skipped: insufficient credits', true);
   764→                    } else if (analyzeResponse.status === 401) {
   765→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   766→                    } else {
   767→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   768→                    }
   769→                }
   770→            } catch (e) {
   771→                console.warn('[SPLICE] Takes detection failed:', e.message);
   772→            }
   773→        }
   774→
   775→        // Detect profanity if enabled
   776→        if (settings.enableProfanity) {
   777→            updateProgress(65, 'Detecting profanity...');
   778→            try {
   779→                const profanityResponse = await fetchWithTimeout(
   780→                    `${getBackendUrl()}/profanity`,
   781→                    {
   782→                        method: 'POST',
   783→                        headers: getAuthHeaders(),
   784→                        body: JSON.stringify({
   785→                            wavPath: audioFilePath,
   786→                            language: settings.profanityLanguage || 'en',
   787→                            bleepType: settings.bleepType || 'standard'
   788→                        })
   789→                    }
   790→                );
   791→
   792→                if (profanityResponse.ok) {
   793→                    const profanityData = await profanityResponse.json();
