     1→/**
     2→ * SPLICE CEP Panel Configuration
     3→ * Backend URLs, paths, and utility functions
     4→ */
     5→
     6→// ============================================================================
     7→// DEBUG MODE CONFIGURATION
     8→// ============================================================================
     9→
    10→/**
    11→ * Debug mode flag - enables verbose diagnostic logging
    12→ * Can be enabled via localStorage for troubleshooting:
    13→ *   localStorage.setItem('spliceDebugMode', 'true');
    14→ *
    15→ * Default: false (production mode - minimal logging)
    16→ */
    17→const SPLICE_DEBUG = localStorage.getItem('spliceDebugMode') === 'true';
    18→
    19→/**
    20→ * Debug logging function - only outputs when debug mode is enabled
    21→ * Use this for diagnostic/development logging that should be silent in production
    22→ * @param {...any} args - Arguments to log (prefixed with [SPLICE DEBUG])
    23→ */
    24→function debugLog(...args) {
    25→    if (SPLICE_DEBUG) {
    26→        console.log('[SPLICE DEBUG]', ...args);
    27→    }
    28→}
    29→
    30→/**
    31→ * Debug fetch logging - verbose fetch request/response logging
    32→ * @param {...any} args - Arguments to log (prefixed with [SPLICE FETCH])
    33→ */
    34→function debugFetch(...args) {
    35→    if (SPLICE_DEBUG) {
    36→        console.log('[SPLICE FETCH]', ...args);
    37→    }
    38→}
    39→
    40→// ============================================================================
    41→// BACKEND CONFIGURATION
    42→// ============================================================================
    43→const SPLICE_CONFIG = {
    44→    VERSION: '6.0.3',
    45→    BACKEND_PROD: 'https://splice-api-production.up.railway.app',
    46→    BACKEND_DEV: 'https://127.0.0.1:3847',
    47→    FETCH_TIMEOUT: 120000, // 2 minutes
    48→    RETRY_ATTEMPTS: 3,
    49→    RETRY_DELAY: 1000
    50→};
    51→
    52→/**
    53→ * Get the backend URL based on environment
    54→ * FIXED: CEP panels always run from file://, so we default to PROD
    55→ * Use localStorage to override for local development if needed
    56→ */
    57→function getBackendUrl() {
    58→    // Priority 1: Allow override via localStorage (for local dev testing)
    59→    const customUrl = localStorage.getItem('spliceBackendUrl');
    60→    if (customUrl) {
    61→        debugLog('Using custom backend URL from localStorage:', customUrl);
    62→        return customUrl;
    63→    }
    64→
    65→    // Priority 2: Default to production backend
    66→    // (CEP panels always run from file://, so can't reliably detect dev mode)
    67→    debugLog('Using production backend URL:', SPLICE_CONFIG.BACKEND_PROD);
    68→    return SPLICE_CONFIG.BACKEND_PROD;
    69→}
    70→
    71→// ============================================================================
    72→// JSX BRIDGE - Communicate with Premiere Pro
    73→// ============================================================================
    74→const jsx = {
    75→    cs: null,
    76→    _initPromise: null,
    77→    _initialized: false,
    78→
    79→    /**
    80→     * Initialize the CSInterface
    81→     * Returns a promise that resolves when ready (true/false)
    82→     * Does NOT reject - gracefully handles missing CEP environment
    83→     */
    84→    init: function() {
    85→        if (this._initPromise) return this._initPromise;
    86→
    87→        this._initPromise = new Promise((resolve) => {
    88→            try {
    89→                if (typeof CSInterface !== 'undefined') {
    90→                    this.cs = new CSInterface();
    91→                    // Verify we're in a real CEP environment by checking for __adobe_cep__
    92→                    if (window.__adobe_cep__) {
    93→                        this._initialized = true;
    94→                        debugLog('[JSX] CSInterface initialized successfully (CEP environment detected)');
    95→                    } else {
    96→                        // CSInterface exists but no CEP runtime - browser/dev mode
    97→                        this._initialized = true;
    98→                        debugLog('[JSX] CSInterface initialized (development/browser mode)');
    99→                    }
   100→                } else {
   101→                    console.warn('[JSX] CSInterface not available - panel may not function correctly');
   102→                    this._initialized = false;
   103→                }
   104→            } catch (err) {
   105→                console.error('[JSX] Failed to initialize CSInterface:', err);
   106→                this._initialized = false;
   107→            }
   108→            resolve(this._initialized);
   109→        });
   110→
   111→        return this._initPromise;
   112→    },
   113→
   114→    /**
   115→     * Ensure JSX is initialized before making calls
   116→     */
   117→    ensureInitialized: async function() {
   118→        if (!this._initialized) {
   119→            await this.init();
   120→        }
   121→        if (!this.cs) {
   122→            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
   123→        }
   124→    },
   125→
   126→    /**
   127→     * Execute ExtendScript in Premiere Pro
   128→     * @param {string} script - ExtendScript to execute
   129→     * @returns {Promise<any>} - Parsed result
   130→     */
   131→    evalScript: async function(script) {
   132→        await this.ensureInitialized();
   133→
   134→        return new Promise((resolve, reject) => {
   135→            this.cs.evalScript(script, (result) => {
   136→                if (result === 'undefined' || result === undefined) {
   137→                    resolve(null);
   138→                    return;
   139→                }
   140→
   141→                // Check for EvalScript error or ExtendScript runtime errors
   142→                if (typeof result === 'string' && (
   143→                    result.indexOf('EvalScript error') !== -1 ||
   144→                    result.indexOf('Error:') !== -1 ||
   145→                    result.indexOf('TypeError:') !== -1 ||
   146→                    result.indexOf('ReferenceError:') !== -1
   147→                )) {
   148→                    reject(new Error(result));
   149→                    return;
   150→                }
   151→
   152→                // Try to parse JSON
   153→                try {
   154→                    const parsed = JSON.parse(result);
   155→                    if (parsed.error) {
   156→                        reject(new Error(parsed.error));
   157→                    } else {
   158→                        resolve(parsed);
   159→                    }
   160→                } catch (e) {
   161→                    // Return as-is if not JSON
   162→                    resolve(result);
   163→                }
   164→            });
   165→        });
   166→    },
   167→
   168→    /**
   169→     * Call a JSX function with arguments
   170→     * @param {string} funcName - Function name
   171→     * @param {...any} args - Arguments to pass
   172→     * @returns {Promise<any>}
   173→     */
   174→    call: async function(funcName, ...args) {
   175→        await this.ensureInitialized();
   176→
   177→        const escapedArgs = args.map(arg => {
   178→            if (arg === null || arg === undefined) return 'null';
   179→            if (typeof arg === 'string') {
   180→                // Escape backslashes first, then other special chars
   181→                return `'${arg
   182→                    .replace(/\\/g, '\\\\')
   183→                    .replace(/'/g, "\\'")
   184→                    .replace(/\r/g, '\\r')
   185→                    .replace(/\n/g, '\\n')
   186→                    .replace(/\t/g, '\\t')}'`;
   187→            }
   188→            if (typeof arg === 'object') {
   189→                return `'${JSON.stringify(arg)
   190→                    .replace(/\\/g, '\\\\')
   191→                    .replace(/'/g, "\\'")}'`;
   192→            }
   193→            return String(arg);
   194→        });
   195→
   196→        const script = `${funcName}(${escapedArgs.join(', ')})`;
   197→        return this.evalScript(script);
   198→    }
   199→};
   200→
   201→// ============================================================================
   202→// FETCH UTILITIES
   203→// ============================================================================
   204→
   205→/**
   206→ * Fetch with timeout support
   207→ * @param {string} url - URL to fetch
   208→ * @param {object} options - Fetch options
   209→ * @param {number} timeout - Timeout in ms
   210→ */
   211→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   212→    // DIAGNOSTIC: Log fetch request details (debug mode only)
   213→    debugFetch('=== FETCH REQUEST START ===');
   214→    debugFetch('URL:', url);
   215→    debugFetch('Method:', options?.method || 'GET');
   216→    debugFetch('Headers:', options?.headers);
   217→    debugFetch('Timeout:', timeout);
   218→    debugFetch('Is CEP environment:', typeof CSInterface !== 'undefined');
   219→    debugFetch('=================================');
   220→
   221→    const controller = new AbortController();
   222→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   223→
   224→    try {
   225→        const response = await fetch(url, {
   226→            ...options,
   227→            signal: controller.signal
   228→        });
   229→        clearTimeout(timeoutId);
   230→
   231→        // DIAGNOSTIC: Log successful response (debug mode only)
   232→        debugFetch('=== FETCH RESPONSE SUCCESS ===');
   233→        debugFetch('Status:', response.status);
   234→        debugFetch('OK:', response.ok);
   235→        debugFetch('=================================');
   236→
   237→        return response;
   238→    } catch (error) {
   239→        clearTimeout(timeoutId);
   240→
   241→        // DIAGNOSTIC: Log raw fetch error (debug mode only)
   242→        debugFetch('=== FETCH ERROR CAUGHT ===');
   243→        debugFetch('Error name:', error?.name);
   244→        debugFetch('Error message:', error?.message);
   245→        debugFetch('Error type:', typeof error);
   246→        debugFetch('Error constructor:', error?.constructor?.name);
   247→        debugFetch('Is AbortError:', error.name === 'AbortError');
   248→        debugFetch('Is TypeError:', error.name === 'TypeError');
   249→        debugFetch('Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
   250→        debugFetch('=================================');
   251→
   252→        if (error.name === 'AbortError') {
   253→            throw new Error(`Request timeout after ${timeout}ms`);
   254→        }
   255→        throw error;
   256→    }
   257→}
   258→
   259→/**
   260→ * Parse error response from backend
   261→ * @param {Response} response - Fetch response
   262→ */
   263→async function parseErrorResponse(response) {
   264→    try {
   265→        const data = await response.json();
   266→        return data.error || data.message || `HTTP ${response.status}`;
   267→    } catch {
   268→        return `HTTP ${response.status}: ${response.statusText}`;
   269→    }
   270→}
   271→
   272→/**
   273→ * Get auth headers for API requests
   274→ * Uses JWT Bearer token if available, with legacy fallback
   275→ */
   276→function getAuthHeaders() {
   277→    const headers = { 'Content-Type': 'application/json' };
   278→    const settings = getSettings();
   279→
   280→    // Prefer JWT Bearer token (modern auth)
   281→    if (settings.accessToken) {
   282→        headers['Authorization'] = `Bearer ${settings.accessToken}`;
   283→        return headers;
   284→    }
   285→
   286→    // Legacy fallback for backwards compatibility
   287→    // This will be removed after 2026-04-11
   288→    if (settings.customerId) {
   289→        headers['x-stripe-customer-id'] = settings.customerId;
   290→        console.warn('[SPLICE] Using deprecated x-stripe-customer-id header. Please re-login to upgrade to JWT auth.');
   291→    }
   292→
   293→    return headers;
   294→}
   295→
   296→/**
   297→ * Check if the access token is expired or about to expire
   298→ * @returns {boolean} True if token needs refresh
   299→ */
   300→function isTokenExpired() {
   301→    const settings = getSettings();
   302→    if (!settings.tokenExpiry) return true;
   303→
   304→    // Refresh if less than 5 minutes remaining
   305→    const bufferMs = 5 * 60 * 1000;
   306→    return Date.now() >= (settings.tokenExpiry - bufferMs);
   307→}
   308→
   309→/**
   310→ * Refresh the access token using the refresh token
   311→ * @returns {Promise<boolean>} True if refresh succeeded
   312→ */
   313→async function refreshAccessToken() {
   314→    const settings = getSettings();
   315→
   316→    if (!settings.refreshToken) {
   317→        debugLog('[Auth] No refresh token available');
   318→        return false;
   319→    }
   320→
   321→    try {
   322→        debugLog('[Auth] Refreshing access token...');
   323→
   324→        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   325→            method: 'POST',
   326→            headers: { 'Content-Type': 'application/json' },
   327→            body: JSON.stringify({ refreshToken: settings.refreshToken })
   328→        }, 30000);
   329→
   330→        if (!response.ok) {
   331→            const errorMsg = await parseErrorResponse(response);
   332→            console.error('[Auth] Token refresh failed:', errorMsg);
   333→
   334→            // If refresh token is invalid, clear auth and require re-login
   335→            if (response.status === 401) {
   336→                clearAuthTokens();
   337→            }
   338→            return false;
   339→        }
   340→
   341→        const data = await response.json();
   342→
   343→        // Save new access token
   344→        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   345→        saveSettings({
   346→            accessToken: data.token,
   347→            tokenExpiry: Date.now() + expiresInMs
   348→        });
   349→
   350→        debugLog('[Auth] Access token refreshed successfully');
   351→        return true;
   352→    } catch (err) {
   353→        console.error('[Auth] Token refresh error:', err);
   354→        return false;
   355→    }
   356→}
   357→
   358→/**
   359→ * Clear all auth tokens (call on logout or auth failure)
   360→ */
   361→function clearAuthTokens() {
   362→    saveSettings({
   363→        accessToken: null,
   364→        refreshToken: null,
   365→        tokenExpiry: null
   366→    });
   367→    debugLog('[Auth] Auth tokens cleared');
   368→}
   369→
   370→/**
   371→ * Ensure we have a valid access token, refreshing if needed
   372→ * @returns {Promise<boolean>} True if we have a valid token
   373→ */
   374→async function ensureValidToken() {
   375→    const settings = getSettings();
   376→
   377→    // No token at all - need to login
   378→    if (!settings.accessToken) {
   379→        return false;
   380→    }
   381→
   382→    // Token not expired - good to go
   383→    if (!isTokenExpired()) {
   384→        return true;
   385→    }
   386→
   387→    // Try to refresh
   388→    return await refreshAccessToken();
   389→}
