   701→// UTILITY FUNCTIONS
   702→// ============================================================================
   703→
   704→/**
   705→ * SECURITY: Escape HTML to prevent XSS attacks
   706→ * Use this when inserting untrusted content into innerHTML
   707→ * @param {string} str - String to escape
   708→ * @returns {string} Escaped string safe for innerHTML
   709→ */
   710→function escapeHtml(str) {
   711→    if (str === null || str === undefined) return '';
   712→    if (typeof str !== 'string') str = String(str);
   713→
   714→    const escapeMap = {
   715→        '&': '&amp;',
   716→        '<': '&lt;',
   717→        '>': '&gt;',
   718→        '"': '&quot;',
   719→        "'": '&#x27;',
   720→        '/': '&#x2F;',
   721→        '`': '&#x60;'
   722→    };
   723→
   724→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   725→}
   726→
   727→/**
   728→ * SECURITY: Create safe HTML from template with escaped values
   729→ * @param {TemplateStringsArray} strings - Template strings
   730→ * @param {...any} values - Values to escape
   731→ * @returns {string} HTML with escaped values
   732→ */
   733→function safeHtml(strings, ...values) {
   734→    return strings.reduce((result, str, i) => {
   735→        const value = values[i - 1];
   736→        const escaped = escapeHtml(value);
   737→        return result + escaped + str;
   738→    });
   739→}
   740→
   741→/**
   742→ * Format time in MM:SS or HH:MM:SS
   743→ */
   744→function formatTime(seconds) {
   745→    if (isNaN(seconds) || seconds < 0) return '0:00';
   746→
   747→    const hours = Math.floor(seconds / 3600);
   748→    const minutes = Math.floor((seconds % 3600) / 60);
   749→    const secs = Math.floor(seconds % 60);
   750→
   751→    if (hours > 0) {
   752→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   753→    }
   754→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   755→}
