   757→/**
   758→ * Set status message
   759→ */
   760→function setStatus(message, isError = false) {
   761→    const status = document.getElementById('status');
   762→    if (status) {
   763→        status.textContent = message;
   764→        status.style.color = isError ? '#dc3545' : '#888';
   765→    }
   766→}
   767→
   768→/**
   769→ * Debounce function calls
   770→ */
   771→function debounce(func, wait) {
   772→    let timeout;
   773→    return function executedFunction(...args) {
   774→        const later = () => {
   775→            clearTimeout(timeout);
   776→            func(...args);
   777→        };
   778→        clearTimeout(timeout);
   779→        timeout = setTimeout(later, wait);
   780→    };
   781→}
