     1→/**
     2→ * SPLICE Backend Server
     3→ *
     4→ * Main entry point for the SPLICE backend API.
     5→ * Orchestrates the audio analysis pipeline.
     6→ *
     7→ * Slices:
     8→ * - Slice 4: Transcription (services/transcription.js)
     9→ * - Slice 5: Take Detection (services/takeDetection.js)
    10→ */
    11→
    12→require('dotenv').config();
    13→
    14→const express = require('express');
    15→const cors = require('cors');
    16→const fs = require('fs');
    17→const https = require('https');
    18→const http = require('http');
    19→const path = require('path');
    20→
    21→// Check if running in production (Railway injects RAILWAY_ENVIRONMENT)
    22→const isProduction = process.env.NODE_ENV === 'production' || process.env.RAILWAY_ENVIRONMENT;
    23→
    24→// Import slice services
    25→const { transcribeAudio } = require('./services/transcription');
    26→const { detectTakes } = require('./services/takeDetection');
    27→const { detectSilences } = require('./services/silenceDetection');
    28→const { detectAudioSilences, isFFprobeInstalled, getAudioDuration } = require('./services/ffprobeSilence');
    29→const { processXMLFile } = require('./services/xmlProcessor');
    30→const { isolateVocals, isReplicateConfigured } = require('./services/vocalIsolation');
    31→const { generateCutList, generateTakesCutList, validateCutList } = require('./services/cutListGenerator');
    32→
    33→// Usage tracking and billing
    34→const usageTracking = require('./services/usageTracking');
    35→// Rate limiter available for future use
    36→// const { requireCredits } = require('./middleware/rateLimiter');
    37→
    38→// Stripe for webhooks
    39→const Stripe = require('stripe');
    40→const stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
    41→
    42→// =============================================================================
    43→// Server Configuration
    44→// =============================================================================
    45→
    46→const app = express();
    47→const PORT = process.env.PORT || 3847;
    48→
    49→// HTTPS certificates (generated by mkcert) - only for local development
    50→let httpsOptions = null;
    51→if (!isProduction) {
    52→  const keyPath = path.join(__dirname, 'localhost+1-key.pem');
    53→  const certPath = path.join(__dirname, 'localhost+1.pem');
    54→  if (fs.existsSync(keyPath) && fs.existsSync(certPath)) {
    55→    httpsOptions = {
    56→      key: fs.readFileSync(keyPath),
    57→      cert: fs.readFileSync(certPath)
    58→    };
    59→  }
    60→}
    61→
    62→app.use(cors());
    63→
    64→// Helper to determine tier from price ID with logging
    65→function getTierFromPriceId(priceId) {
    66→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
    67→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
    68→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
    69→
    70→  // Log unknown price ID for debugging
    71→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
    72→  return 'starter';
    73→}
    74→
    75→// Stripe webhook needs raw body - must be before express.json()
    76→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
    77→  const sig = req.headers['stripe-signature'];
    78→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
    79→
    80→  let event;
    81→
    82→  try {
    83→    if (webhookSecret) {
    84→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
    85→    } else {
    86→      // For testing without webhook secret
    87→      event = JSON.parse(req.body);
    88→      console.warn('[SPLICE] Warning: Processing webhook without signature verification');
    89→    }
    90→  } catch (err) {
    91→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
    92→    return res.status(400).json({ error: 'Webhook signature verification failed' });
    93→  }
    94→
    95→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
    96→
    97→  // Idempotency check - skip if already processed
    98→  if (await usageTracking.isEventProcessed(event.id)) {
    99→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   100→    return res.json({ received: true, skipped: true });
   101→  }
   102→
   103→  try {
   104→    switch (event.type) {
   105→      case 'customer.subscription.created':
   106→      case 'customer.subscription.updated': {
   107→        const subscription = event.data.object;
   108→        const customerId = subscription.customer;
   109→
   110→        // Validate customerId
   111→        if (!customerId) {
   112→          console.error('[SPLICE] Missing customer ID in subscription event');
   113→          return res.status(400).json({ error: 'Missing customer ID' });
   114→        }
   115→
   116→        // Get tier from price ID
   117→        const priceId = subscription.items?.data?.[0]?.price?.id;
   118→        const tier = getTierFromPriceId(priceId);
   119→
   120→        // Update user tier and reset hours
   121→        await usageTracking.updateTier(customerId, tier);
   122→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   123→        break;
   124→      }
   125→
   126→      case 'customer.subscription.deleted': {
   127→        const subscription = event.data.object;
   128→        const customerId = subscription.customer;
   129→
   130→        // Validate customerId
   131→        if (!customerId) {
   132→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   133→          return res.status(400).json({ error: 'Missing customer ID' });
   134→        }
   135→
   136→        // Downgrade to cancelled (0 hours)
   137→        await usageTracking.updateTier(customerId, 'cancelled');
   138→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   139→        break;
   140→      }
   141→
   142→      case 'invoice.payment_succeeded': {
   143→        const invoice = event.data.object;
   144→        const customerId = invoice.customer;
   145→        const subscriptionId = invoice.subscription;
   146→
   147→        // Validate customerId
   148→        if (!customerId) {
   149→          console.error('[SPLICE] Missing customer ID in invoice event');
   150→          return res.status(400).json({ error: 'Missing customer ID' });
   151→        }
   152→
   153→        // Reset hours on successful payment (new billing period)
   154→        if (subscriptionId) {
   155→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   156→          const priceId = subscription.items?.data?.[0]?.price?.id;
   157→          const tier = getTierFromPriceId(priceId);
   158→
   159→          await usageTracking.resetHours(customerId, tier);
   160→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   161→        }
   162→        break;
   163→      }
   164→
   165→      default:
   166→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   167→    }
   168→
   169→    // Record event as processed (idempotency)
   170→    await usageTracking.recordWebhookEvent(event.id, event.type);
   171→
   172→    res.json({ received: true });
   173→  } catch (err) {
   174→    console.error('[SPLICE] Webhook handler error:', err);
   175→    res.status(500).json({ error: err.message });
   176→  }
   177→});
   178→
   179→app.use(express.json());
   180→
   181→// =============================================================================
   182→// Routes
   183→// =============================================================================
   184→
   185→/**
   186→ * GET / - API information
   187→ */
   188→app.get('/', (req, res) => {
   189→  res.json({
   190→    service: 'splice-backend',
   191→    version: '0.3.0',
   192→    endpoints: {
   193→      'GET /': 'This info',
   194→      'GET /health': 'Health check',
   195→      'GET /ffprobe-check': 'Check if FFprobe is installed',
   196→      'GET /replicate-check': 'Check if Replicate API is configured',
   197→      'POST /analyze': 'Analyze WAV file { wavPath }',
   198→      'POST /silences': 'Detect silences via Whisper gaps { wavPath, threshold: 0.5 }',
   199→      'POST /silences-audio': 'Detect silences via FFprobe { wavPath, threshold: -30, minDuration: 0.5, padding: 0.1 }',
   200→      'POST /process-xml': 'Process FCP XML { xmlPath, silences, removeGaps: true }',
   201→      'POST /cut-list': 'Generate JSON cut list for DOM building (v3.5) { sourceName, sourcePath, duration, silences, takes?, settings? }',
   202→      'POST /cut-list/takes': 'Generate cut list keeping only takes { sourceName, sourcePath, duration, takes, settings? }',
   203→      'POST /isolate-vocals': 'Isolate vocals from audio { audioPath }',
   204→      'GET /credits': 'Get user credit balance (requires x-stripe-customer-id header)',
   205→      'GET /usage-history': 'Get usage history (requires x-stripe-customer-id header)',
   206→      'POST /webhooks/stripe': 'Stripe webhook endpoint'
   207→    }
   208→  });
   209→});
   210→
   211→/**
   212→ * GET /health - Health check
   213→ */
   214→app.get('/health', (req, res) => {
   215→  res.json({ status: 'ok', service: 'splice-backend' });
   216→});
   217→
   218→/**
   219→ * POST /analyze - Main analysis endpoint
   220→ *
   221→ * Pipeline:
   222→ * 1. Validate input (wavPath)
   223→ * 2. Slice 4: Transcribe audio with Groq Whisper
   224→ * 3. Slice 5: Detect takes with GPT-4o-mini
   225→ * 4. Return combined results
   226→ */
   227→app.post('/analyze', async (req, res) => {
   228→  const { wavPath } = req.body;
   229→
   230→  // Validate input
   231→  if (!wavPath) {
   232→    return res.status(400).json({ error: 'wavPath is required' });
   233→  }
   234→
   235→  if (!fs.existsSync(wavPath)) {
   236→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   237→  }
   238→
   239→  console.log(`[SPLICE] Analyzing: ${wavPath}`);
   240→
   241→  try {
   242→    // Slice 4 - GPT-4o-mini transcription
   243→    const transcript = await transcribeAudio(wavPath);
   244→
   245→    // Slice 5 - GPT-4o-mini take detection
   246→    const takes = await detectTakes(transcript);
   247→
   248→    res.json({
   249→      success: true,
   250→      wavPath,
   251→      transcript,
   252→      takes
   253→    });
   254→  } catch (err) {
   255→    console.error('[SPLICE] Error:', err);
   256→    res.status(500).json({ error: err.message });
   257→  }
   258→});
   259→
   260→/**
   261→ * POST /silences - Detect silent gaps in audio
   262→ *
   263→ * Pipeline:
   264→ * 1. Transcribe audio with Whisper (reuses transcription)
   265→ * 2. Analyze gaps between segments
   266→ * 3. Return silence regions
   267→ */
   268→app.post('/silences', async (req, res) => {
   269→  const { wavPath, threshold = 0.5 } = req.body;
   270→
   271→  if (!wavPath) {
   272→    return res.status(400).json({ error: 'wavPath is required' });
   273→  }
   274→
   275→  if (!fs.existsSync(wavPath)) {
   276→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   277→  }
   278→
   279→  console.log(`[SPLICE] Detecting silences: ${wavPath} (threshold: ${threshold}s)`);
   280→
   281→  try {
   282→    const transcript = await transcribeAudio(wavPath);
   283→    const silences = detectSilences(transcript.segments, threshold);
   284→
   285→    res.json({
   286→      success: true,
   287→      wavPath,
   288→      threshold,
   289→      silences,
   290→      count: silences.length,
   291→      totalSilenceDuration: silences.reduce((sum, s) => sum + s.duration, 0).toFixed(2)
   292→    });
   293→  } catch (err) {
   294→    console.error('[SPLICE] Silence detection error:', err);
   295→    res.status(500).json({ error: err.message });
   296→  }
   297→});
   298→
   299→/**
   300→ * POST /silences-audio - Detect silences using FFprobe audio analysis
   301→ *
   302→ * Uses actual audio levels (dB threshold) instead of transcript gaps.
   303→ * More accurate for detecting silence vs background noise.
   304→ */
   305→app.post('/silences-audio', async (req, res) => {
   306→  const {
   307→    wavPath,
   308→    threshold = -30,
   309→    minDuration = 0.5,
   310→    padding = 0.1
   311→  } = req.body;
   312→
   313→  if (!wavPath) {
   314→    return res.status(400).json({ error: 'wavPath is required' });
   315→  }
   316→
   317→  if (!fs.existsSync(wavPath)) {
   318→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   319→  }
   320→
   321→  // Check FFprobe availability
   322→  const ffprobeAvailable = await isFFprobeInstalled();
   323→  if (!ffprobeAvailable) {
   324→    return res.status(500).json({
   325→      error: 'FFprobe not installed. Run: brew install ffmpeg'
   326→    });
   327→  }
   328→
   329→  console.log(`[SPLICE] FFprobe silence detection: ${wavPath} (threshold: ${threshold}dB, min: ${minDuration}s)`);
   330→
   331→  try {
   332→    const silences = await detectAudioSilences(wavPath, {
   333→      threshold,
   334→      minDuration,
   335→      padding
   336→    });
   337→
   338→    const totalDuration = silences.reduce((sum, s) => sum + s.duration, 0);
   339→
   340→    res.json({
   341→      success: true,
   342→      wavPath,
   343→      threshold,
   344→      minDuration,
   345→      padding,
   346→      silences,
   347→      count: silences.length,
   348→      totalSilenceDuration: totalDuration.toFixed(2)
   349→    });
   350→  } catch (err) {
   351→    console.error('[SPLICE] FFprobe silence detection error:', err);
   352→    res.status(500).json({ error: err.message });
   353→  }
   354→});
   355→
   356→/**
   357→ * POST /process-xml - Process FCP XML to split clips at silences
   358→ *
   359→ * Takes an FCP XML file and silence timestamps, splits clips
   360→ * at silence boundaries, and optionally removes gaps.
   361→ */
   362→app.post('/process-xml', async (req, res) => {
   363→  const {
   364→    xmlPath,
   365→    silences,
   366→    removeGaps = true,
   367→    outputPath = null
   368→  } = req.body;
   369→
   370→  if (!xmlPath) {
   371→    return res.status(400).json({ error: 'xmlPath is required' });
   372→  }
   373→
   374→  if (!silences || !Array.isArray(silences)) {
   375→    return res.status(400).json({ error: 'silences array is required' });
   376→  }
   377→
   378→  if (!fs.existsSync(xmlPath)) {
   379→    return res.status(404).json({ error: `XML file not found: ${xmlPath}` });
   380→  }
   381→
   382→  console.log(`[SPLICE] Processing XML: ${xmlPath} with ${silences.length} silence(s)`);
   383→
   384→  try {
   385→    const result = await processXMLFile(xmlPath, silences, {
   386→      outputPath,
   387→      removeGaps
   388→    });
   389→
   390→    res.json({
   391→      success: true,
   392→      inputPath: xmlPath,
   393→      outputPath: result.outputPath,
   394→      stats: result.stats
   395→    });
   396→  } catch (err) {
   397→    console.error('[SPLICE] XML processing error:', err);
   398→    res.status(500).json({ error: err.message });
   399→  }
   400→});
   401→
   402→/**
   403→ * POST /cut-list - Generate a JSON cut list for direct DOM building (v3.5)
   404→ *
   405→ * Takes silences and optionally takes, returns a cut list that the
   406→ * plugin can use to build sequences directly via UXP APIs.
   407→ *
   408→ * Body:
   409→ * - sourceName: Name of the source clip
   410→ * - sourcePath: Full path to the source file
   411→ * - duration: Total duration in seconds
   412→ * - silences: Array of silence segments [{start, end, duration}]
   413→ * - takes: (optional) Array of detected takes
   414→ * - settings: (optional) Generation settings
   415→ */
   416→app.post('/cut-list', async (req, res) => {
   417→  const {
   418→    sourceName,
   419→    sourcePath,
   420→    duration,
   421→    silences,
   422→    takes = [],
   423→    settings = {}
   424→  } = req.body;
   425→
   426→  // Validate required fields
   427→  if (!sourceName && !sourcePath) {
   428→    return res.status(400).json({ error: 'sourceName or sourcePath is required' });
   429→  }
   430→
   431→  if (typeof duration !== 'number' || duration <= 0) {
   432→    return res.status(400).json({ error: 'duration must be a positive number' });
   433→  }
   434→
   435→  if (!silences || !Array.isArray(silences)) {
   436→    return res.status(400).json({ error: 'silences array is required' });
   437→  }
   438→
   439→  console.log(`[SPLICE] Generating cut list for ${sourceName || sourcePath} (${silences.length} silences)`);
   440→
   441→  try {
   442→    const cutList = generateCutList({
   443→      sourceName: sourceName || path.basename(sourcePath),
   444→      sourcePath,
   445→      duration,
   446→      silences,
   447→      takes,
   448→      settings
   449→    });
   450→
   451→    // Validate the generated cut list
   452→    const validation = validateCutList(cutList);
   453→    if (!validation.valid) {
   454→      return res.status(500).json({
   455→        error: 'Generated cut list is invalid',
   456→        validationErrors: validation.errors
   457→      });
   458→    }
   459→
   460→    res.json({
   461→      success: true,
   462→      cutList
   463→    });
   464→  } catch (err) {
   465→    console.error('[SPLICE] Cut list generation error:', err);
   466→    res.status(500).json({ error: err.message });
   467→  }
   468→});
   469→
   470→/**
   471→ * POST /cut-list/takes - Generate a cut list that keeps only takes
   472→ *
   473→ * Alternative endpoint for "keep best takes only" workflow.
   474→ */
   475→app.post('/cut-list/takes', async (req, res) => {
   476→  const {
   477→    sourceName,
   478→    sourcePath,
   479→    duration,
   480→    takes,
   481→    settings = {}
   482→  } = req.body;
   483→
   484→  // Validate required fields
   485→  if (!sourceName && !sourcePath) {
   486→    return res.status(400).json({ error: 'sourceName or sourcePath is required' });
   487→  }
   488→
   489→  if (typeof duration !== 'number' || duration <= 0) {
   490→    return res.status(400).json({ error: 'duration must be a positive number' });
   491→  }
   492→
   493→  if (!takes || !Array.isArray(takes) || takes.length === 0) {
   494→    return res.status(400).json({ error: 'takes array is required and must not be empty' });
   495→  }
   496→
   497→  console.log(`[SPLICE] Generating takes cut list for ${sourceName || sourcePath} (${takes.length} takes)`);
   498→
   499→  try {
   500→    const cutList = generateTakesCutList({
   501→      sourceName: sourceName || path.basename(sourcePath),
   502→      sourcePath,
   503→      duration,
   504→      takes,
   505→      settings
   506→    });
   507→
   508→    res.json({
   509→      success: true,
   510→      cutList
   511→    });
   512→  } catch (err) {
   513→    console.error('[SPLICE] Takes cut list generation error:', err);
   514→    res.status(500).json({ error: err.message });
   515→  }
   516→});
   517→
   518→/**
   519→ * GET /ffprobe-check - Check if FFprobe is installed
   520→ */
   521→app.get('/ffprobe-check', async (req, res) => {
   522→  const installed = await isFFprobeInstalled();
   523→  res.json({
   524→    installed,
   525→    message: installed
   526→      ? 'FFprobe is available'
   527→      : 'FFprobe not found. Install with: brew install ffmpeg'
   528→  });
   529→});
   530→
   531→/**
   532→ * GET /replicate-check - Check if Replicate API is configured
   533→ */
   534→app.get('/replicate-check', async (req, res) => {
   535→  const configured = isReplicateConfigured();
   536→  res.json({
   537→    configured,
   538→    message: configured
   539→      ? 'Replicate API is configured'
   540→      : 'REPLICATE_API_TOKEN not set. Add to .env file.'
   541→  });
   542→});
   543→
   544→/**
   545→ * POST /isolate-vocals - Isolate vocals from audio using Demucs
   546→ *
   547→ * Uses Replicate's Demucs model to separate vocals from background audio.
   548→ * Cost: ~$0.015/min of audio
   549→ *
   550→ * Tier access:
   551→ * - Starter: No access (upgrade required)
   552→ * - Pro: 2 hours included, then $0.08/min overage
   553→ * - Team: 5 hours included, then $0.08/min overage
   554→ */
   555→app.post('/isolate-vocals', async (req, res) => {
   556→  const { audioPath, stem = 'vocals', outputDir = null } = req.body;
   557→  const stripeCustomerId = req.headers['x-stripe-customer-id'];
   558→
   559→  if (!audioPath) {
   560→    return res.status(400).json({ error: 'audioPath is required' });
   561→  }
   562→
   563→  if (!fs.existsSync(audioPath)) {
   564→    return res.status(404).json({ error: `File not found: ${audioPath}` });
   565→  }
   566→
   567→  // Check Replicate configuration
   568→  if (!isReplicateConfigured()) {
   569→    return res.status(500).json({
   570→      error: 'Replicate API not configured. Set REPLICATE_API_TOKEN in .env'
   571→    });
   572→  }
   573→
   574→  // Get audio duration for billing
   575→  let audioDurationSeconds = 0;
   576→  try {
   577→    audioDurationSeconds = await getAudioDuration(audioPath);
   578→  } catch (err) {
   579→    console.warn('[SPLICE] Could not get audio duration:', err.message);
   580→  }
   581→
   582→  const audioDurationMinutes = audioDurationSeconds / 60;
   583→
   584→  // Check isolation access if customer ID provided
   585→  if (stripeCustomerId) {
   586→    const accessCheck = await usageTracking.checkIsolationAccess(stripeCustomerId, audioDurationMinutes);
   587→
   588→    if (!accessCheck.allowed) {
   589→      return res.status(403).json({
   590→        error: accessCheck.message,
   591→        reason: accessCheck.reason,
   592→        upgradeRequired: accessCheck.reason === 'upgrade_required'
   593→      });
   594→    }
   595→
   596→    console.log(`[SPLICE] Isolation access: ${accessCheck.message}`);
   597→  }
   598→
   599→  console.log(`[SPLICE] Isolating vocals: ${audioPath} (${audioDurationMinutes.toFixed(1)} min)`);
   600→
   601→  try {
   602→    const result = await isolateVocals(audioPath, {
   603→      stem,
   604→      outputDir: outputDir || undefined
   605→    });
   606→
   607→    // Deduct isolation usage if customer ID provided
   608→    let usageInfo = null;
   609→    if (stripeCustomerId) {
   610→      usageInfo = await usageTracking.deductIsolationUsage(
   611→        stripeCustomerId,
   612→        audioDurationSeconds,
   613→        'isolate-vocals'
   614→      );
   615→      console.log(`[SPLICE] Isolation usage deducted: ${audioDurationMinutes.toFixed(1)} min`);
