  1128→/**
  1129→ * Build a sequence from detection data locally
  1130→ * Used for testing via console: await buildSequenceV35(silences)
  1131→ */
  1132→async function buildSequenceV35(silences, takes = []) {
  1133→    if (!window.spliceBuilder) {
  1134→        throw new Error('Builder not initialized. Check if builder.js is loaded.');
  1135→    }
  1136→
  1137→    const app = require('premierepro');
  1138→    const project = await app.Project.getActiveProject();
  1139→    if (!project) throw new Error("No active project found.");
  1140→
  1141→    const rootItem = await project.getRootItem();
  1142→    const sourceProjectItem = await findFirstMediaItem(rootItem);
  1143→
  1144→    if (!sourceProjectItem) {
  1145→        throw new Error('No media items found in project bin.');
  1146→    }
  1147→
  1148→    console.log(`[SPLICE] Found source media: ${sourceProjectItem.name}`);
  1149→
  1150→    return await window.spliceBuilder.buildSequenceFromDetection(silences, takes, sourceProjectItem);
  1151→}
