  1153→/**
  1154→ * Request a cut list from the backend and build the sequence.
  1155→ * Full v3.5 workflow.
  1156→ */
  1157→async function buildSequenceWithCutList() {
  1158→  if (!window.spliceBuilder) {
  1159→    throw new Error('Builder not initialized');
  1160→  }
  1161→
  1162→  // Ensure BACKEND_URL is available
  1163→  const apiUrl = (typeof BACKEND_URL !== 'undefined') ? BACKEND_URL : 'http://localhost:3000';
  1164→
  1165→  if (typeof showProgress === 'function') showProgress('Getting source clip info...');
  1166→
  1167→  const app = require('premierepro');
  1168→  const project = await app.Project.getActiveProject();
  1169→  if (!project) throw new Error("No active project.");
  1170→
  1171→  const rootItem = await project.getRootItem();
  1172→  const sourceProjectItem = await findFirstMediaItem(rootItem);
  1173→
  1174→  if (!sourceProjectItem) {
  1175→    throw new Error('No media items found in project. Please import a media file first.');
  1176→  }
  1177→
  1178→  // Use properties strictly
  1179→  const sourceName = sourceProjectItem.name;
  1180→  const sourcePath = sourceProjectItem.treePath;
  1181→
  1182→  const durationObj = await sourceProjectItem.getDuration();
  1183→  const duration = durationObj ? durationObj.seconds : 0;
  1184→
  1185→  // Retrieve Globals
  1186→  const silences = window.splicePreviewState?.getSelectedSilences() || (typeof safeSilences !== 'undefined' ? safeSilences : []);
  1187→  const takes = (typeof previewTakes !== 'undefined' ? previewTakes : []);
  1188→
  1189→  if (silences.length === 0 && (!takes || takes.length === 0)) {
  1190→     throw new Error('No detection data found. Run analysis first.');
  1191→  }
  1192→
  1193→  if (typeof showProgress === 'function') showProgress('Generating cut list...');
  1194→
  1195→  const response = await fetch(`${apiUrl}/cut-list`, {
  1196→    method: 'POST',
  1197→    headers: { 'Content-Type': 'application/json' },
  1198→    body: JSON.stringify({
  1199→      sourceName,
  1200→      sourcePath,
  1201→      duration,
  1202→      silences,
  1203→      takes
  1204→    })
  1205→  });
  1206→
  1207→  if (!response.ok) {
  1208→    const err = await response.json();
  1209→    throw new Error(err.error || 'Failed to generate cut list');
  1210→  }
  1211→
  1212→  const data = await response.json();
  1213→  const cutList = data.cutList;
  1214→
  1215→  if (typeof showProgress === 'function') showProgress('Building sequence...');
  1216→
  1217→  const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, sourceProjectItem);
  1218→
  1219→  if (!result.success) {
  1220→    throw new Error(result.error || 'Failed to build sequence');
  1221→  }
  1222→
  1223→  return {
  1224→    success: true,
  1225→    clipsAdded: result.clipsAdded,
  1226→    metadata: cutList.metadata
  1227→  };
  1228→}
  1229→
  1230→// Expose functions globally for UI and testing
  1231→if (typeof window !== 'undefined') {
  1232→    window.buildSequenceV35 = buildSequenceV35;
  1233→    window.buildSequenceWithCutList = buildSequenceWithCutList;
  1234→    window.findFirstMediaItem = findFirstMediaItem;
  1235→}
