  1237→// ============================================================================
  1238→// TIMELINE SEEK (with proper error handling)
  1239→// ============================================================================
  1240→async function seekToTime(seconds) {
  1241→  // Validate input
  1242→  if (typeof seconds !== 'number' || isNaN(seconds) || !isFinite(seconds)) {
  1243→    setStatus('Invalid seek time');
  1244→    return;
  1245→  }
  1246→
  1247→  if (seconds < 0) {
  1248→    seconds = 0;
  1249→  }
  1250→
  1251→  try {
  1252→    const context = await getActiveSequence();
  1253→    if (!context) {
  1254→      setStatus('No sequence available');
  1255→      return;
  1256→    }
  1257→
  1258→    const { sequence } = context;
  1259→
  1260→    // Use TickTime API to convert seconds to the format needed
  1261→    const position = ppro.TickTime.createWithSeconds(seconds);
