// OFFLINE DETECTION
// ============================================================================

// Track online/offline state
let _isOnline = typeof navigator !== 'undefined' ? navigator.onLine : true;
let _offlineCallbacks = [];

/**
 * Check if the browser/app is online
 * @returns {boolean} True if online
 */
function isOnline() {
  // navigator.onLine can be undefined in some UXP contexts
  if (typeof navigator !== 'undefined' && typeof navigator.onLine === 'boolean') {
    return navigator.onLine;
  }
  return _isOnline;
}

/**
 * Register a callback for online/offline state changes
 * @param {Function} callback - Called with (isOnline: boolean) on state change
 * @returns {Function} Unsubscribe function
 */
function onOnlineStateChange(callback) {
  _offlineCallbacks.push(callback);
  return () => {
    _offlineCallbacks = _offlineCallbacks.filter(cb => cb !== callback);
  };

