        }
      }
    });

    // Initialize collapsed state
    if (ui.zoomSettings) {
      ui.zoomSettings.style.display = ui.enableZoom.checked ? 'block' : 'none';
    }
  }

  // Apply zooms button
  if (ui.applyZoomsBtn) {
    ui.applyZoomsBtn.addEventListener('click', applyZoomsToTimeline);
  }

  console.log('[SPLICE] Auto Zoom UI initialized');
