 */
async function seekToTime(time) {
  try {
    const context = await getActiveSequence();
    if (context && context.sequence && typeof context.sequence.setPlayerPosition === 'function') {
      const { TickTime } = require('premierepro');
      const tickTime = TickTime.createWithSeconds(time);
      await context.sequence.setPlayerPosition(tickTime);
      console.log(`[SPLICE] Seeked to ${time.toFixed(2)}s`);
    }
  } catch (err) {
    console.log('[SPLICE] Seek not available:', err.message);

