  // Keyboard shortcuts
  initKeyboardShortcuts();

  // Advanced features (Razor - legacy)
  initSlice9();

  // Batch processing
  initBatchHandlers();

  // Multitrack editing (Phase 2)
  if (typeof initMultitrackUI === 'function') {
    initMultitrackUI();
  }

  // Media folder (v3.5)
  initMediaFolderHandlers();

  // Custom Presets UI (v3.5)
  initCustomPresetsUI();

  // J-Cut/L-Cut UI (Phase 1)
  initJCutUI();

  // Auto Zoom UI (Phase 3)
  initZoomUI();

  // Chapter Detection UI (Phase 3)
  initChapterUI();

  // Profanity Settings UI (Feature 5)
  if (typeof initProfanitySettingsUI === 'function') {
