    if (ui.zoomSettings) {
      ui.zoomSettings.style.display = ui.enableZoom.checked ? 'block' : 'none';
    }
  }

  // Apply zooms button
  if (ui.applyZoomsBtn) {
    ui.applyZoomsBtn.addEventListener('click', applyZoomsToTimeline);
  }

