   385→/**
   386→ * Set status message
   387→ */
   388→function setStatus(message, isError = false) {
   389→    const status = document.getElementById('status');
   390→    if (status) {
   391→        status.textContent = message;
   392→        status.style.color = isError ? '#dc3545' : '#888';
   393→    }
