   396→/**
   397→ * Debounce function calls
   398→ */
   399→function debounce(func, wait) {
   400→    let timeout;
   401→    return function executedFunction(...args) {
   402→        const later = () => {
   403→            clearTimeout(timeout);
   404→            func(...args);
   405→        };
   406→        clearTimeout(timeout);
   407→        timeout = setTimeout(later, wait);
   408→    };
   409→}
