"use client";

import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { motion } from "framer-motion";
import { Check, HelpCircle, Users, TrendingUp } from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { useState, useEffect } from "react";

const plans = [
  {
    name: "Starter",
    price: "$15",
    originalPrice: "$25",
    period: "/month",
    yearlyPrice: "$144",
    yearlyPeriod: "/year",
    yearlySavings: "Save $36",
    savings: "Save 40%",
    description: "Perfect for individual creators getting started",
    features: [
      { name: "4 hours of processing/month", included: true },
      { name: "AI silence removal (multiple methods)", included: true },
      { name: "Smart take detection", included: true },
      { name: "2 AI music generations/month", included: true },
      { name: "Basic caption templates (2)", included: true },
      { name: "SRT/VTT caption export", included: true },
      { name: "Filler word detection", included: true },
      { name: "Email support", included: true },
      { name: "Vocal isolation", included: false },
      { name: "Social reframe", included: false },
      { name: "Text-based editing", included: false },
      { name: "Multitrack auto-balance", included: false },
    ],
    cta: "Start Free Trial",
    popular: false,
    spotsLeft: 47,
    priceId: "price_1SgH4m2L5BseYEbCih347qfr",
  },
  {
    name: "Pro",
    price: "$39",
    originalPrice: "$65",
    period: "/month",
    yearlyPrice: "$374",
    yearlyPeriod: "/year",
    yearlySavings: "Save $94",
    savings: "Save 40%",
    description: "For serious creators who need more power",
    features: [
      { name: "15 hours of processing/month", included: true },
      { name: "Everything in Starter, plus:", included: true, highlight: true },
      { name: "45 min vocal isolation included", included: true },
      { name: "10 AI music generations/month", included: true },
      { name: "All 5 caption templates (MrBeast, Hormozi, etc.)", included: true },
      { name: "Social reframe with face tracking", included: true },
      { name: "Text-based editing", included: true },
      { name: "Profanity detection", included: true },
      { name: "Chapter detection", included: true },
      { name: "Priority support", included: true },
      { name: "Multitrack auto-balance", included: false },
    ],
    cta: "Start Free Trial",
    popular: true,
    spotsLeft: 23,
    priceId: "price_1SgH4o2L5BseYEbCIoR19lhQ",
  },
  {
    name: "Team",
    price: "$129",
    originalPrice: "$215",
    period: "/month",
    yearlyPrice: "$1,238",
    yearlyPeriod: "/year",
    yearlySavings: "Save $310",
    savings: "Save 40%",
    description: "For teams and high-volume creators",
    features: [
      { name: "50 hours of processing/month", included: true },
      { name: "Everything in Pro, plus:", included: true, highlight: true },
      { name: "3 hours vocal isolation included", included: true },
      { name: "50 AI music generations/month", included: true },
      { name: "Multitrack/multicam auto-balance", included: true },
      { name: "Scene-aware music generation", included: true },
      { name: "Music-to-cut alignment", included: true },
      { name: "Mood timeline generation", included: true },
      { name: "YouTube metadata generation", included: true },
      { name: "Profanity bleeping (5 types)", included: true },
      { name: "Dedicated account manager", included: true },
    ],
    cta: "Contact Sales",
    popular: false,
    spotsLeft: 8,
    priceId: "price_1SgH4q2L5BseYEbCYTlimb5M",
  },
];

const overageRates = [
  { item: "Processing hours", rate: "$2.00/hour" },
  { item: "Vocal isolation", rate: "$0.10/minute" },
  { item: "Music generation", rate: "$1.00/song" },
];

const pricingFaqs = [
  {
    question: "What counts as a 'processing hour'?",
    answer:
      "Processing hours are calculated based on the duration of the video/audio you analyze. If you process a 30-minute video, that uses 0.5 hours of your monthly quota. Transcription, silence detection, take detection, and other analysis features all count toward this limit.",
  },
  {
    question: "Can I switch plans mid-month?",
    answer:
      "Yes! You can upgrade at any time and the difference will be prorated. If you downgrade, the change takes effect at the start of your next billing cycle.",
  },
  {
    question: "What happens if I exceed my limits?",
    answer:
      "You can continue using SPLICE with overage billing. We'll notify you when you're at 80% and 100% of your limit. Overage charges are billed at the end of each month.",
  },
  {
    question: "Do unused hours roll over?",
    answer:
      "No, monthly hours reset at the start of each billing cycle. We recommend choosing a plan that matches your typical monthly usage.",
  },
  {
    question: "Is there a free tier?",
    answer:
      "We offer a 14-day free trial on all plans with full access to features. No credit card required to start.",
  },
  {
    question: "Do you offer annual billing?",
    answer:
      "Yes! Annual billing saves you about 2 months compared to monthly billing. Contact us for annual pricing.",
  },
];

// Helper function for calculating time left (defined outside component to avoid recreating)
function getTimeLeft() {
  const now = new Date();
  const endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0, 23, 59, 59);
  const difference = endDate.getTime() - now.getTime();
  if (difference > 0) {
    return {
      days: Math.floor(difference / (1000 * 60 * 60 * 24)),
      hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
      minutes: Math.floor((difference / 1000 / 60) % 60),
      seconds: Math.floor((difference / 1000) % 60),
    };
  }
  return { days: 0, hours: 0, minutes: 0, seconds: 0 };
}

// Countdown timer component
function UrgencyCountdown() {
  const [timeLeft, setTimeLeft] = useState(getTimeLeft);

  useEffect(() => {
    const timer = setInterval(() => setTimeLeft(getTimeLeft()), 1000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="flex items-center justify-center gap-3 text-sm">
      {[
        { value: timeLeft.days, label: "Days" },
        { value: timeLeft.hours, label: "Hrs" },
        { value: timeLeft.minutes, label: "Min" },
        { value: timeLeft.seconds, label: "Sec" },
      ].map((item, i) => (
        <div key={item.label} className="flex items-center gap-1">
          <div className="bg-[#00D4FF]/20 text-[#00D4FF] font-mono font-bold px-2 py-1 rounded">
            {String(item.value).padStart(2, "0")}
          </div>
          <span className="text-gray-500 font-medium text-xs">{item.label}</span>
          {i < 3 && <span className="text-gray-400 ml-1">:</span>}
        </div>
      ))}
    </div>
  );
}

export default function PricingPage() {
  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24">
        {/* Hero section */}
        <section className="py-12 bg-muted/10 relative overflow-hidden">
          {/* Background gradient */}
          <div className="absolute inset-0 bg-gradient-to-b from-transparent to-[#060b14]/50 pointer-events-none" />

          <div className="container mx-auto px-4 relative z-10">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              {/* Urgency banner */}
              <div className="inline-flex items-center gap-3 mb-6 px-4 py-2 rounded-full bg-gradient-to-r from-orange-500/20 to-red-500/20 border border-orange-500/30">
                <span className="flex h-2 w-2">
                  <span className="animate-ping absolute inline-flex h-2 w-2 rounded-full bg-orange-400 opacity-75"></span>
                  <span className="relative inline-flex rounded-full h-2 w-2 bg-orange-500"></span>
                </span>
                <span className="text-sm font-medium text-orange-400">
                  Beta Pricing Ends Soon — Lock in 40% savings forever
                </span>
              </div>

              <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
                Pricing That Pays for <span className="gradient-text">Itself</span>
              </h1>
              <p className="text-lg text-gray-400 max-w-2xl mx-auto mb-6">
                Join 10,000+ editors saving hours every week. No hidden fees.
              </p>

              {/* Countdown timer */}
              <div className="mb-8">
                <p className="text-sm text-gray-500 mb-2">Beta pricing expires in:</p>
                <UrgencyCountdown />
              </div>
            </motion.div>
          </div>
        </section>

        {/* Pricing cards */}
        <section className="py-8 pb-16">
          <div className="container mx-auto px-4">
            <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
              {plans.map((plan, index) => (
                <motion.div
                  key={plan.name}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                  className={plan.popular ? "md:-mt-4 md:mb-4" : ""}
                >
                  <Card
                    className={`h-full relative bg-[#0d1420] ${plan.popular
                        ? "border-[#00D4FF] shadow-lg shadow-[#00D4FF]/20"
                        : "border-gray-800"
                      }`}
                  >
                    {plan.popular && (
                      <div className="absolute -top-3 left-1/2 -translate-x-1/2">
                        <Badge className="bg-[#00D4FF] text-black font-semibold hover:bg-[#00B8E6]">
                          Most Popular
                        </Badge>
                      </div>
                    )}

                    <CardHeader className="text-center pb-2 pt-8">
                      <div className="flex items-center justify-center gap-2 mb-1">
                        <h3 className="text-xl font-bold text-white">{plan.name}</h3>
                        {plan.savings && (
                          <Badge className="bg-green-500/20 text-green-400 border-green-500/30 text-xs">
                            {plan.savings}
                          </Badge>
                        )}
                      </div>
                      <div className="mt-4">
                        {plan.originalPrice && (
                          <span className="text-lg text-gray-500 line-through mr-2">{plan.originalPrice}</span>
                        )}
                        <span className="text-4xl font-bold text-white">{plan.price}</span>
                        <span className="text-gray-400">{plan.period}</span>
                      </div>
                      <p className="text-sm text-gray-400 mt-2">
                        {plan.description}
                      </p>
                      {/* Spots remaining urgency */}
                      {plan.spotsLeft && (
                        <div className="mt-3 flex items-center justify-center gap-1.5 text-xs">
                          <span className="flex h-1.5 w-1.5">
                            <span className="animate-ping absolute inline-flex h-1.5 w-1.5 rounded-full bg-orange-400 opacity-75"></span>
                            <span className="relative inline-flex rounded-full h-1.5 w-1.5 bg-orange-500"></span>
                          </span>
                          <span className="text-orange-400">
                            Only <span className="font-bold">{plan.spotsLeft}</span> beta spots left at this price
                          </span>
                        </div>
                      )}
                    </CardHeader>

                    <CardContent className="pt-4">
                      <ul className="space-y-3 mb-8">
                        {plan.features.map((feature) => (
                          <li key={feature.name} className="flex items-start gap-2">
                            {feature.included ? (
                              <Check className="w-5 h-5 text-[#00D4FF] shrink-0 mt-0.5" />
                            ) : (
                              <div className="w-5 h-5 shrink-0 mt-0.5 flex items-center justify-center">
                                <div className="w-1.5 h-1.5 rounded-full bg-gray-800" />
                              </div>
                            )}
                            <span
                              className={`text-sm ${feature.included
                                  ? feature.highlight
                                    ? "font-semibold text-white"
                                    : "text-gray-300"
                                  : "text-gray-600"
                                }`}
                            >
                              {feature.name}
                            </span>
                          </li>
                        ))}
                      </ul>

                      <Button
                        className={`w-full ${plan.popular
                            ? "bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                            : "bg-transparent border-gray-700 text-white hover:bg-white/10"
                          }`}
                        variant={plan.popular ? "default" : "outline"}
                        size="lg"
                        asChild
                      >
                        <Link href={plan.cta === "Contact Sales" ? "/contact" : `/checkout?plan=${plan.name.toLowerCase()}`}>
                          {plan.cta}
                        </Link>
                      </Button>
                      <p className="text-xs text-center text-gray-500 mt-3">
                        14-day money-back guarantee. Cancel anytime.
                      </p>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}
            </div>

            {/* Social proof strip below cards */}
            <div className="mt-12 flex flex-wrap items-center justify-center gap-8 text-sm text-gray-400">
              <div className="flex items-center gap-2">
                <Users className="w-4 h-4 text-[#00D4FF]" />
                <span><span className="text-white font-semibold">2,847</span> editors joined this week</span>
              </div>
              <div className="flex items-center gap-2">
                <TrendingUp className="w-4 h-4 text-green-400" />
                <span><span className="text-white font-semibold">94%</span> of users upgrade after trial</span>
              </div>
            </div>
          </div>
        </section>

        {/* Overage rates */}
        <section className="py-16 bg-[#0d1420]/50 border-t border-gray-800">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              className="max-w-3xl mx-auto"
            >
              <div className="text-center mb-8">
                <h2 className="text-2xl md:text-3xl font-bold mb-2 text-white">
                  Overage Rates
                </h2>
                <p className="text-gray-400">
                  Need more than your plan includes? No problem.
                </p>
              </div>

              <Card className="bg-[#060b14] border-gray-800">
                <CardContent className="p-0">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b border-gray-800">
                        <th className="text-left p-4 font-medium text-gray-300">Item</th>
                        <th className="text-right p-4 font-medium text-gray-300">Rate</th>
                      </tr>
                    </thead>
                    <tbody>
                      {overageRates.map((rate, index) => (
                        <tr
                          key={rate.item}
                          className={index % 2 === 0 ? "bg-[#0d1420]" : ""}
                        >
                          <td className="p-4 text-gray-300">{rate.item}</td>
                          <td className="p-4 text-right font-mono text-sm text-[#00D4FF]">
                            {rate.rate}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </CardContent>
              </Card>
            </motion.div>
          </div>
        </section>

        {/* FAQ */}
        <section className="py-16">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              className="max-w-3xl mx-auto"
            >
              <div className="text-center mb-8">
                <HelpCircle className="w-10 h-10 mx-auto mb-4 text-[#00D4FF]" />
                <h2 className="text-2xl md:text-3xl font-bold mb-2 text-white">
                  Pricing FAQ
                </h2>
                <p className="text-gray-400">
                  Common questions about our pricing
                </p>
              </div>

              <Accordion type="single" collapsible className="w-full">
                {pricingFaqs.map((faq, index) => (
                  <AccordionItem key={index} value={`item-${index}`} className="border-gray-800">
                    <AccordionTrigger className="text-left hover:no-underline text-gray-200 hover:text-[#00D4FF] transition-colors">
                      {faq.question}
                    </AccordionTrigger>
                    <AccordionContent className="text-gray-400">
                      {faq.answer}
                    </AccordionContent>
                  </AccordionItem>
                ))}
              </Accordion>
            </motion.div>
          </div>
        </section>

        {/* CTA */}
        <section className="py-16 bg-gradient-to-br from-violet-900/50 to-cyan-900/50 border-t border-[#00D4FF]/20">
          <div className="container mx-auto px-4 text-center text-white">
            <h2 className="text-3xl font-bold mb-4">
              Ready to Edit 10x Faster?
            </h2>
            <p className="text-lg opacity-90 mb-8 max-w-xl mx-auto text-gray-300">
              Start your 14-day free trial today. No credit card required.
            </p>
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold px-8 h-12"
              asChild
            >
              <Link href="/checkout?plan=pro">Start Free Trial</Link>
            </Button>
            <p className="mt-4 text-xs text-gray-500">
              Full access for 14 days. No obligation.
            </p>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

