              <Card className="bg-[#060b14] border-gray-800">
                <CardContent className="p-0">
                  <table className="w-full">
                    <thead>
                      <tr className="border-b border-gray-800">
                        <th className="text-left p-4 font-medium text-gray-300">Item</th>
                        <th className="text-right p-4 font-medium text-gray-300">Rate</th>
                      </tr>
                    </thead>
                    <tbody>
                      {overageRates.map((rate, index) => (
                        <tr
                          key={rate.item}
                          className={index % 2 === 0 ? "bg-[#0d1420]" : ""}
                        >
                          <td className="p-4 text-gray-300">{rate.item}</td>
                          <td className="p-4 text-right font-mono text-sm text-[#00D4FF]">
                            {rate.rate}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </CardContent>
              </Card>
            </motion.div>
          </div>
        </section>

        {/* FAQ */}
        <section className="py-16">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              className="max-w-3xl mx-auto"
            >
              <div className="text-center mb-8">
                <HelpCircle className="w-10 h-10 mx-auto mb-4 text-[#00D4FF]" />
                <h2 className="text-2xl md:text-3xl font-bold mb-2 text-white">
                  Pricing FAQ
                </h2>
                <p className="text-gray-400">
                  Common questions about our pricing
                </p>
              </div>

              <Accordion type="single" collapsible className="w-full">
                {pricingFaqs.map((faq, index) => (
                  <AccordionItem key={index} value={`item-${index}`} className="border-gray-800">
                    <AccordionTrigger className="text-left hover:no-underline text-gray-200 hover:text-[#00D4FF] transition-colors">
                      {faq.question}
                    </AccordionTrigger>
                    <AccordionContent className="text-gray-400">
                      {faq.answer}
                    </AccordionContent>
                  </AccordionItem>
                ))}
              </Accordion>
            </motion.div>
          </div>
        </section>

        {/* CTA */}
        <section className="py-16 bg-gradient-to-br from-violet-900/50 to-cyan-900/50 border-t border-[#00D4FF]/20">
          <div className="container mx-auto px-4 text-center text-white">
            <h2 className="text-3xl font-bold mb-4">
              Ready to Edit 10x Faster?
            </h2>
            <p className="text-lg opacity-90 mb-8 max-w-xl mx-auto text-gray-300">
              Start your 14-day free trial today. No credit card required.
            </p>
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold px-8 h-12"
              asChild
            >
              <Link href="/checkout?plan=pro">Start Free Trial</Link>
            </Button>
            <p className="mt-4 text-xs text-gray-500">
              Full access for 14 days. No obligation.
            </p>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}
