     1→/* global localStorage */
     2→/**
     3→ * Phase 4: Production Blockers E2E Tests
     4→ *
     5→ * Tests for:
     6→ * 1. Login/Auth UI - customer ID storage and retrieval
     7→ * 2. Auth headers - proper header injection in requests
     8→ * 3. Rate limiting - requireCredits middleware on endpoints
     9→ * 4. Error messages - user-friendly build error formatting
    10→ */
    11→
    12→// Simulate localStorage for Node.js testing
    13→const storage = { data: {} };
    14→global.localStorage = {
    15→  getItem: (key) => storage.data[key] || null,
    16→  setItem: (key, value) => { storage.data[key] = value; },
    17→  removeItem: (key) => { delete storage.data[key]; },
    18→  clear: () => { storage.data = {}; }
    19→};
    20→
    21→function resetLocalStorage() {
    22→  storage.data = {};
    23→}
    24→
    25→// Suppress console noise
    26→const originalWarn = console.warn;
    27→const originalError = console.error;
    28→console.warn = () => {};
    29→console.error = () => {};
    30→
    31→// =============================================================================
    32→// COPY OF SETTINGS FUNCTIONS (for testing without UXP)
    33→// =============================================================================
    34→
    35→const DEFAULT_SETTINGS = {
    36→  sensitivity: 50,
    37→  audioSource: 'original',
    38→  autoMarkBest: true,
    39→  enableTakesDetection: true,
    40→  rememberOptions: false,
    41→  optionsExpanded: false,
    42→  customerId: null,
    43→  mediaFolderToken: null,
    44→  mediaFolderPath: null,
    45→  activePreset: 'custom'
    46→};
    47→
    48→let currentSettings = { ...DEFAULT_SETTINGS };
    49→
    50→function loadSettings() {
    51→  try {
    52→    const saved = localStorage.getItem('spliceSettings');
    53→    if (saved) {
    54→      currentSettings = { ...DEFAULT_SETTINGS, ...JSON.parse(saved) };
    55→    }
    56→  } catch (_e) {
    57→    currentSettings = { ...DEFAULT_SETTINGS };
    58→  }
    59→  return currentSettings;
    60→}
    61→
    62→function saveSettings(settings) {
    63→  try {
    64→    currentSettings = { ...currentSettings, ...settings };
    65→    localStorage.setItem('spliceSettings', JSON.stringify(currentSettings));
    66→  } catch (_e) {
    67→    // Silent fail
    68→  }
    69→}
    70→
    71→function getSettings() {
    72→  return { ...currentSettings };
    73→}
    74→
    75→function isLoggedIn() {
    76→  const settings = getSettings();
    77→  return !!settings.customerId;
    78→}
    79→
    80→function getCustomerId() {
    81→  const settings = getSettings();
    82→  return settings.customerId || null;
    83→}
    84→
    85→function logout() {
    86→  saveSettings({ customerId: null });
    87→}
    88→
    89→// =============================================================================
    90→// COPY OF AUTH HEADERS FUNCTION (from main.js)
    91→// =============================================================================
    92→
    93→function getAuthHeaders() {
    94→  const headers = { 'Content-Type': 'application/json' };
    95→  const settings = getSettings();
    96→  if (settings.customerId) {
    97→    headers['x-stripe-customer-id'] = settings.customerId;
    98→  }
    99→  return headers;
   100→}
   101→
   102→// =============================================================================
   103→// COPY OF ERROR FORMATTING FUNCTION (from main.js)
   104→// =============================================================================
   105→
   106→function formatBuildError(error) {
   107→  if (!error) return 'Unknown error occurred';
   108→
   109→  const errorMap = {
   110→    'Builder not initialized': 'Builder module failed to load. Try restarting the plugin.',
   111→    'No active project': 'No project is open. Please open or create a project first.',
   112→    'No media items found': 'No media files in project. Import media before building.',
   113→    'No detection data': 'Run analysis first (click GO button).',
   114→    'Failed to generate cut list': 'Server error generating cuts. Check your connection.',
   115→    'Failed to build sequence': 'Premiere Pro sequence build failed. Try again.',
   116→    'fetch failed': 'Network error. Check if backend server is running.',
   117→    'NetworkError': 'Cannot connect to server. Check your internet connection.',
   118→    'Insufficient credits': 'Out of credits. Upgrade your plan to continue.',
   119→    'Authentication required': 'Login required. Click the credits badge to log in.'
   120→  };
   121→
   122→  for (const [pattern, friendly] of Object.entries(errorMap)) {
   123→    if (error.includes(pattern)) {
   124→      return friendly;
   125→    }
   126→  }
   127→
   128→  return error;
   129→}
   130→
   131→// =============================================================================
   132→// TEST FRAMEWORK
   133→// =============================================================================
   134→
   135→let passCount = 0;
   136→let failCount = 0;
   137→
   138→function test(name, fn) {
   139→  try {
   140→    fn();
   141→    passCount++;
   142→    console.log(`  ✓ ${name}`);
   143→  } catch (err) {
   144→    failCount++;
   145→    console.log(`  ✗ ${name}`);
   146→    console.log(`    Error: ${err.message}`);
   147→  }
   148→}
   149→
   150→function assertEqual(actual, expected, message = '') {
   151→  if (actual !== expected) {
   152→    throw new Error(`${message}: Expected ${JSON.stringify(expected)}, got ${JSON.stringify(actual)}`);
   153→  }
   154→}
   155→
   156→function assertTrue(condition, message = '') {
   157→  if (!condition) {
   158→    throw new Error(`${message}: Expected true, got false`);
