   173→}
   174→
   175→// eslint-disable-next-line no-unused-vars -- available for tests
   176→function assertIncludes(arr, item, message = '') {
   177→  if (!arr || !arr.includes) {
   178→    throw new Error(`${message}: Not an array`);
   179→  }
   180→  if (!arr.includes(item)) {
   181→    throw new Error(`${message}: Array does not include ${item}`);
   182→  }
   183→}
   184→
   185→// =============================================================================
   186→// TEST SUITES
   187→// =============================================================================
   188→
   189→console.log('\n=== Phase 4: Production Blockers E2E Tests ===\n');
   190→
   191→// -----------------------------------------------------------------------------
   192→// Test Suite 1: Login/Auth UI - Customer ID Storage
   193→// -----------------------------------------------------------------------------
   194→
   195→console.log('1. Login/Auth UI - Customer ID Storage');
   196→
   197→test('should start with no customer ID', () => {
   198→  resetLocalStorage();
   199→  currentSettings = { ...DEFAULT_SETTINGS };
   200→  loadSettings();
