/**
 * POST /license/lookup - Look up license key by email address
 *
 * Allows users to retrieve their license key using their purchase email.
 * This is the primary recovery method when email delivery fails.
 */
app.post('/license/lookup', async (req, res) => {
  const { email } = req.body;

  if (!email) {
    return res.status(400).json({
      error: 'Email address required'
    });
  }

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(email)) {
    return res.status(400).json({
      error: 'Invalid email format'
    });
  }

  try {
    // Look up customer by email in Stripe
    const customers = await stripe.customers.list({
      email: email.toLowerCase(),
      limit: 1
    });

    if (customers.data.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'No subscription found for this email',
        message: 'Please check your email or purchase a subscription at splice.app'
      });
    }

    const customer = customers.data[0];

    // Get license key for this customer
    const licenseResult = await licenseService.getLicenseByCustomerId(customer.id);

    if (!licenseResult.success) {
      // Check if they have an active subscription before generating key
      const subscriptions = await stripe.subscriptions.list({
        customer: customer.id,
        status: 'active',
        limit: 1
      });

      if (subscriptions.data.length === 0) {
        return res.status(404).json({
          success: false,
          error: 'No active subscription found',
          message: 'Your subscription may have expired. Please renew at splice.app'
        });
      }

      // Generate a new license key for active subscriber without one
      const newLicense = await licenseService.generateLicenseKey(customer.id);
      if (!newLicense.success) {
        return res.status(500).json({
          success: false,
          error: 'Failed to generate license key'
        });
      }

      return res.json({
        success: true,
        key: newLicense.key,
        email: email,
        message: 'License key generated. Copy this key and paste it in the plugin.'
      });
    }

    // Return existing key
    res.json({
      success: true,
      key: licenseResult.key,
      email: email,
      activated: licenseResult.activated,
      message: licenseResult.activated
        ? 'Your license is already activated. You can use this key on another device.'
        : 'Copy this key and paste it in the SPLICE plugin to activate.'
    });
  } catch (err) {
    console.error('[SPLICE] License lookup by email error:', err);
    res.status(500).json({ error: 'Failed to look up license' });
  }
});

