app.post('/multitrack', requireCredits({ endpoint: 'multitrack' }), async (req, res) => {
  const {
    audioPaths,
    speakerNames,
    videoTrackMapping = {},
    minShotDuration = 2.0,
    switchingFrequency = 50,
    wideShotEnabled = true,
