    if (audioDuration > 0 && req.deductUsage) {
      balance = await req.deductUsage(audioDuration);
    }

    res.json({
      success: true,
      zoomPoints,
      count: zoomPoints.length,
      settings: {
        frequency: settings.frequency || 'medium',
        preset: settings.preset || 'medium',
