      status: job.status,
      createdAt: job.createdAt,
      completedAt: job.completedAt,
      progress: job.progress
    }));

  // Sort by creation date (newest first)
  jobs.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

