   517→/**
   518→ * Check if user can use vocal isolation
   519→ * Returns: { allowed, hasIncludedHours, overageRequired, overageCost }
   520→ */
   521→async function checkIsolationAccess(stripeCustomerId, estimatedMinutes = 0) {
   522→  const balance = await getBalance(stripeCustomerId);
   523→
   524→  // Starter tier has no isolation access
   525→  if (!balance.hasIsolationAccess) {
   526→    return {
   527→      allowed: false,
   528→      reason: 'upgrade_required',
   529→      message: 'Vocal isolation requires Pro or Team tier',
   530→      hasIncludedHours: false,
   531→      overageRequired: false,
   532→      overageCost: 0
   533→    };
   534→  }
   535→
   536→  const estimatedHours = estimatedMinutes / 60;
   537→  const remainingIncluded = balance.isolationHoursRemaining;
   538→
   539→  // Check if within included hours
   540→  if (remainingIncluded >= estimatedHours) {
   541→    return {
   542→      allowed: true,
   543→      reason: 'included',
   544→      message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   545→      hasIncludedHours: true,
   546→      overageRequired: false,
   547→      overageCost: 0,
   548→      isolationHoursRemaining: remainingIncluded - estimatedHours
   549→    };
   550→  }
   551→
   552→  // Calculate overage
   553→  const overageMinutes = estimatedMinutes - (remainingIncluded * 60);
   554→  const overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   555→
   556→  return {
   557→    allowed: true,
   558→    reason: 'overage',
   559→    message: `${remainingIncluded > 0 ? `Using ${(remainingIncluded * 60).toFixed(1)} included min + ` : ''}${overageMinutes.toFixed(1)} min overage ($${overageCost.toFixed(2)})`,
   560→    hasIncludedHours: remainingIncluded > 0,
   561→    overageRequired: true,
   562→    overageMinutes,
   563→    overageCost,
   564→    isolationHoursRemaining: 0
   565→  };
   566→}
