   275→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   276→  const sig = req.headers['stripe-signature'];
   277→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   278→
   279→  let event;
