   332→        }
   333→
   334→        // Generate license key for new subscriptions with retry and delivery
   335→        if (event.type === 'customer.subscription.created') {
   336→          let licenseResult = null;
   337→          let retryCount = 0;
   338→          const maxRetries = 3;
