   672→    if (isProduction || !httpsOptions) {
   673→      // Production: Railway provides TLS termination, use HTTP
   674→      server = http.createServer(app).listen(PORT, () => {
   675→        console.log(`[SPLICE] Backend running at http://0.0.0.0:${PORT} (production)`);
   676→      });
   677→    } else {
   678→      // Development: Use HTTPS with local certificates
   679→      server = https.createServer(httpsOptions, app).listen(PORT, () => {
   680→        console.log(`[SPLICE] Backend running at https://127.0.0.1:${PORT} (development)`);
   681→        console.log(`[SPLICE] POST /analyze with { "wavPath": "/path/to/audio.wav" }`);
   682→      });
   683→    }
   684→  } catch (err) {
   685→    console.error('[SPLICE] Failed to start server:', err);
   686→    process.exit(1);
   687→  }
   688→}
   689→
   690→// =============================================================================
   691→// Graceful Shutdown
   692→// =============================================================================
   693→
   694→async function gracefulShutdown(signal) {
   695→  console.log(`[SPLICE] Received ${signal}, starting graceful shutdown...`);
   696→
   697→  // Stop accepting new connections
   698→  if (server) {
   699→    server.close(() => {
   700→      console.log('[SPLICE] HTTP server closed');
   701→    });
   702→  }
   703→
   704→  // Close database pool
   705→  try {
   706→    await usageTracking.closePool();
   707→    console.log('[SPLICE] Database pool closed');
   708→  } catch (err) {
   709→    console.error('[SPLICE] Error closing database pool:', err.message);
   710→  }
   711→
   712→  // Give ongoing requests time to complete (max 10 seconds)
   713→  setTimeout(() => {
   714→    console.log('[SPLICE] Graceful shutdown complete');
   715→    process.exit(0);
   716→  }, 10000);
   717→}
   718→
   719→// Handle termination signals
   720→process.on('SIGTERM', () => gracefulShutdown('SIGTERM'));
   721→process.on('SIGINT', () => gracefulShutdown('SIGINT'));
   722→
   723→// Handle uncaught exceptions
   724→process.on('uncaughtException', (err) => {
   725→  console.error('[SPLICE] Uncaught exception:', err);
   726→  gracefulShutdown('uncaughtException');
   727→});
   728→
   729→process.on('unhandledRejection', (reason, promise) => {
