   431→function applyPreset(presetId) {
   432→    const preset = PRESETS[presetId];
   433→    if (!preset) return;
   434→
   435→    // Custom preset - keep current settings
   436→    if (presetId === 'custom' || !preset.settings) {
   437→        saveSettings({ activePreset: 'custom' });
   438→        setStatus('Using custom settings');
   439→        return;
   440→    }
   441→
   442→    const settings = preset.settings;
   443→
   444→    // Apply to UI
   445→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   446→        ui.sensitivitySlider.value = settings.sensitivity;
   447→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   448→    }
   449→
   450→    if (ui.enableJCut) {
   451→        ui.enableJCut.checked = settings.enableJCut || false;
   452→        if (ui.jcutSettings) {
   453→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   454→        }
   455→    }
   456→
   457→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   458→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   459→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   460→    }
   461→
   462→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   463→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   464→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   465→    }
   466→
   467→    // Save merged settings
   468→    saveSettings({
   469→        activePreset: presetId,
   470→        sensitivity: settings.sensitivity,
   471→        threshold: settings.threshold,
   472→        minSilenceLength: settings.minSilenceLength,
   473→        paddingStart: settings.paddingStart,
   474→        paddingEnd: settings.paddingEnd,
   475→        enableTakesDetection: settings.enableTakesDetection,
   476→        enableJCut: settings.enableJCut || false,
   477→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   478→        jcutLeadOut: settings.jCutLeadOut || 0.2
   479→    });
   480→
   481→    setStatus(`Applied ${preset.name} preset`);
   482→}
