   529→
   530→async function runDetection() {
   531→    if (isOperationInProgress) return;
   532→
   533→    // Check sequence
   534→    const hasSequence = await checkSequenceOpen();
   535→    if (!hasSequence) {
   536→        setStatus('Please open a sequence first', true);
   537→        return;
   538→    }
   539→
   540→    // Check online
   541→    if (!isOnline()) {
   542→        setStatus('Offline - Check your connection', true);
   543→        return;
   544→    }
   545→
   546→    isOperationInProgress = true;
   547→    ui.goBtn.disabled = true;
   548→    showProgress('Detecting silences...');
   549→
   550→    try {
   551→        const settings = getSettings();
   552→
   553→        // Get sequence info
   554→        const seqInfo = await jsx.call('getActiveSequence');
   555→        console.log('[SPLICE] Sequence info:', seqInfo);
   556→
   557→        // Store sequence info for later use
   558→        currentSequenceInfo = seqInfo;
   559→        window.spliceState.activeSequence = seqInfo;
   560→        if (seqInfo && seqInfo.videoPath) {
   561→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   562→        }
   563→
   564→        // Export audio via JSX for silence detection
   565→        updateProgress(10, 'Exporting audio...');
   566→
   567→        let audioFilePath = null;
   568→        try {
   569→            // Get sequence info for audio export
   570→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   571→            if (exportResult && exportResult.success && exportResult.outputPath) {
   572→                audioFilePath = exportResult.outputPath;
   573→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   574→            } else if (exportResult && exportResult.error) {
   575→                throw new Error(exportResult.error);
   576→            }
   577→        } catch (exportError) {
   578→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   579→            // Try to get audio from first clip in timeline
   580→            try {
   581→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   582→                if (clipInfo && clipInfo.path) {
   583→                    audioFilePath = clipInfo.path;
   584→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   585→                }
   586→            } catch (clipError) {
   587→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   588→            }
   589→        }
   590→
   591→        if (!audioFilePath) {
   592→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   593→        }
   594→
   595→        // Store audio path globally for other modules
   596→        currentAudioPath = audioFilePath;
   597→        window.spliceState.audioPath = audioFilePath;
   598→
   599→        // Call backend for silence detection with audio file path
   600→        updateProgress(30, 'Detecting silences...');
   601→        const silenceResponse = await fetchWithTimeout(
   602→            `${getBackendUrl()}/silences-rms`,
   603→            {
   604→                method: 'POST',
   605→                headers: getAuthHeaders(),
   606→                body: JSON.stringify({
   607→                    wavPath: audioFilePath,
   608→                    sensitivity: settings.sensitivity,
   609→                    minSilenceLength: settings.minSilenceLength || 0.5
   610→                })
   611→            }
   612→        );
   613→
   614→        if (!silenceResponse.ok) {
   615→            throw new Error(await parseErrorResponse(silenceResponse));
   616→        }
   617→
   618→        const silenceData = await silenceResponse.json();
   619→        previewSilences = silenceData.silences || [];
   620→
   621→        // Detect takes if enabled
   622→        if (settings.enableTakesDetection) {
   623→            updateProgress(60, 'Detecting takes...');
   624→            try {
   625→                const analyzeResponse = await fetchWithTimeout(
   626→                    `${getBackendUrl()}/analyze`,
   627→                    {
   628→                        method: 'POST',
   629→                        headers: getAuthHeaders(),
   630→                        body: JSON.stringify({
   631→                            wavPath: audioFilePath,
   632→                            detectTakes: true
   633→                        })
   634→                    }
   635→                );
   636→
   637→                if (analyzeResponse.ok) {
   638→                    const analyzeData = await analyzeResponse.json();
   639→                    previewTakes = analyzeData.takes || [];
   640→
   641→                    // Store transcript for captions/music modules
   642→                    if (analyzeData.transcript) {
   643→                        currentTranscript = analyzeData.transcript;
   644→                        window.currentTranscript = analyzeData.transcript;
   645→                        window.spliceState.lastTranscript = analyzeData.transcript;
   646→                    }
   647→                } else {
   648→                    // Handle specific error codes
   649→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   650→                    if (analyzeResponse.status === 402) {
   651→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   652→                        setStatus('Takes detection skipped: insufficient credits', true);
   653→                    } else if (analyzeResponse.status === 401) {
   654→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   655→                    } else {
   656→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   657→                    }
   658→                }
   659→            } catch (e) {
   660→                console.warn('[SPLICE] Takes detection failed:', e.message);
   661→            }
   662→        }
   663→
   664→        // Detect profanity if enabled
   665→        if (settings.enableProfanity) {
   666→            updateProgress(65, 'Detecting profanity...');
   667→            try {
   668→                const profanityResponse = await fetchWithTimeout(
   669→                    `${getBackendUrl()}/profanity`,
   670→                    {
   671→                        method: 'POST',
   672→                        headers: getAuthHeaders(),
   673→                        body: JSON.stringify({
   674→                            wavPath: audioFilePath,
   675→                            language: settings.profanityLanguage || 'en',
   676→                            bleepType: settings.bleepType || 'standard'
   677→                        })
   678→                    }
   679→                );
   680→
   681→                if (profanityResponse.ok) {
   682→                    const profanityData = await profanityResponse.json();
   683→                    currentProfanity = profanityData.instances || [];
   684→                    if (currentProfanity.length > 0) {
   685→                        console.log(`[SPLICE] Found ${currentProfanity.length} profanity instances`);
   686→                    }
   687→                } else {
   688→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   689→                }
   690→            } catch (e) {
   691→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   692→            }
   693→        }
   694→
   695→        // Detect chapters if enabled
   696→        if (settings.enableChapters) {
   697→            updateProgress(80, 'Detecting chapters...');
   698→            try {
   699→                const chapterResponse = await fetchWithTimeout(
   700→                    `${getBackendUrl()}/chapters`,
   701→                    {
   702→                        method: 'POST',
   703→                        headers: getAuthHeaders(),
   704→                        body: JSON.stringify({
   705→                            wavPath: audioFilePath,
   706→                            transcript: currentTranscript,
   707→                            maxChapters: settings.maxChapters || 10,
   708→                            minChapterLength: settings.minChapterLength || 60
   709→                        })
   710→                    }
   711→                );
   712→
   713→                if (chapterResponse.ok) {
   714→                    const chapterData = await chapterResponse.json();
   715→                    currentChapters = chapterData.chapters || [];
   716→                }
   717→            } catch (e) {
   718→                console.warn('[SPLICE] Chapter detection failed:', e);
   719→            }
   720→        }
   721→
   722→        // Detect zoom points if enabled
   723→        if (settings.enableZoom) {
   724→            updateProgress(90, 'Detecting zoom points...');
   725→            try {
   726→                const zoomResponse = await fetchWithTimeout(
   727→                    `${getBackendUrl()}/zoom`,
   728→                    {
   729→                        method: 'POST',
   730→                        headers: getAuthHeaders(),
   731→                        body: JSON.stringify({
   732→                            wavPath: audioFilePath,
   733→                            transcript: currentTranscript,
   734→                            frequency: settings.zoomFrequency || 'medium',
   735→                            preset: settings.zoomPreset || 'medium',
   736→                            placement: settings.zoomPlacement || 'sentence_start'
   737→                        })
   738→                    }
   739→                );
   740→
   741→                if (zoomResponse.ok) {
   742→                    const zoomData = await zoomResponse.json();
   743→                    currentZooms = zoomData.zoomPoints || [];
   744→                }
   745→            } catch (e) {
   746→                console.warn('[SPLICE] Zoom detection failed:', e);
   747→            }
   748→        }
   749→
   750→        // Store results
   751→        lastDetectionResult = {
   752→            silences: previewSilences,
   753→            takes: previewTakes,
   754→            chapters: currentChapters,
   755→            zooms: currentZooms
   756→        };
   757→
   758→        // Show preview
   759→        hideProgress();
   760→        showCombinedPreview();
   761→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   762→
   763→    } catch (error) {
   764→        console.error('[SPLICE] Detection error:', error);
   765→        setStatus('Detection failed: ' + error.message, true);
   766→        hideProgress();
   767→    } finally {
   768→        isOperationInProgress = false;
   769→        ui.goBtn.disabled = false;
   770→    }
   771→}
