   906→        // Use JSX to set playhead position
   907→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
   908→        const ticks = Math.round(seconds * 254016000000);
   909→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
   910→    } catch (e) {
   911→        console.warn('[SPLICE] Seek failed:', e);
