  1283→    // Basic email validation
  1284→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1285→    if (!emailRegex.test(email)) {
  1286→        setStatus('Please enter a valid email address', true);
  1287→        return;
