"use client";

import { useEffect, useState, Suspense } from "react";
import { useSearchParams } from "next/navigation";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { motion } from "framer-motion";
import {
  CheckCircle,
  Download,
  Mail,
  ArrowRight,
  Loader2,
  Copy,
  Check,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

function SuccessContent() {
  const searchParams = useSearchParams();
  const sessionId = searchParams.get("session_id");

  const [isLoading, setIsLoading] = useState(true);
  const [sessionData, setSessionData] = useState<{
    customerEmail: string;
    plan: string;
  } | null>(null);
  const [copied, setCopied] = useState(false);

  useEffect(() => {
    let cancelled = false;

    const fetchSession = async () => {
      if (!sessionId) {
        if (!cancelled) setIsLoading(false);
        return;
      }

      try {
        const res = await fetch(`/api/checkout?session_id=${sessionId}`);
        const data = await res.json();
        if (!cancelled) {
          setSessionData(data);
          setIsLoading(false);
        }
      } catch {
        if (!cancelled) setIsLoading(false);
      }
    };

    fetchSession();

    return () => { cancelled = true; };
  }, [sessionId]);

  const copyEmail = () => {
    if (sessionData?.customerEmail) {
      navigator.clipboard.writeText(sessionData.customerEmail);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin text-primary" />
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4">
          <div className="max-w-2xl mx-auto text-center">
            {/* Success animation */}
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{
                type: "spring",
                stiffness: 200,
                damping: 15,
              }}
              className="mb-8"
            >
              <div className="w-24 h-24 rounded-full bg-green-500/20 flex items-center justify-center mx-auto">
                <CheckCircle className="w-12 h-12 text-green-500" />
              </div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
            >
              <h1 className="text-3xl md:text-4xl font-bold mb-4">
                Welcome to SPLICE!
              </h1>
              <p className="text-lg text-muted-foreground mb-8">
                Your 14-day free trial has started. Let&apos;s get you set up.
              </p>
            </motion.div>

            {/* Next steps */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.4 }}
              className="space-y-4"
            >
              {/* Email confirmation */}
              <Card>
                <CardContent className="p-6">
                  <div className="flex items-start gap-4">
                    <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center shrink-0">
                      <Mail className="w-5 h-5 text-primary" />
                    </div>
                    <div className="text-left flex-1">
                      <h3 className="font-semibold mb-1">
                        Check your email
                      </h3>
                      <p className="text-sm text-muted-foreground mb-2">
                        We&apos;ve sent your license key to:
                      </p>
                      {sessionData?.customerEmail && (
                        <div className="flex items-center gap-2">
                          <code className="bg-muted px-2 py-1 rounded text-sm">
                            {sessionData.customerEmail}
                          </code>
                          <button
                            onClick={copyEmail}
                            className="p-1 hover:bg-muted rounded"
                            aria-label="Copy email"
                          >
                            {copied ? (
                              <Check className="w-4 h-4 text-green-500" />
                            ) : (
                              <Copy className="w-4 h-4 text-muted-foreground" />
                            )}
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Download */}
              <Card>
                <CardContent className="p-6">
                  <div className="flex items-start gap-4">
                    <div className="w-10 h-10 rounded-full bg-primary/10 flex items-center justify-center shrink-0">
                      <Download className="w-5 h-5 text-primary" />
                    </div>
                    <div className="text-left flex-1">
                      <h3 className="font-semibold mb-1">
                        Download the plugin
                      </h3>
                      <p className="text-sm text-muted-foreground mb-3">
                        Get SPLICE for your version of Premiere Pro
                      </p>
                      <Button
                        variant="outline"
                        size="sm"
                        asChild
                      >
                        <Link href="/download">
                          Go to Downloads
                          <ArrowRight className="w-4 h-4 ml-2" />
                        </Link>
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Plan info */}
              {sessionData?.plan && (
                <Card className="bg-gradient-to-br from-violet-500/10 to-cyan-500/10 border-primary/20">
                  <CardContent className="p-6">
                    <div className="flex items-center justify-between">
                      <div className="text-left">
                        <p className="text-sm text-muted-foreground">Your plan</p>
                        <h3 className="text-xl font-bold capitalize">
                          {sessionData.plan}
                        </h3>
                      </div>
                      <div className="text-right">
                        <p className="text-sm text-muted-foreground">Trial ends in</p>
                        <h3 className="text-xl font-bold text-primary">14 days</h3>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              )}
            </motion.div>

            {/* Quick links */}
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: 0.6 }}
              className="mt-8 flex flex-col sm:flex-row items-center justify-center gap-4"
            >
              <Button
                className="bg-gradient-to-r from-violet-600 to-cyan-600 hover:from-violet-700 hover:to-cyan-700"
                size="lg"
                asChild
              >
                <Link href="/download">
                  <Download className="w-4 h-4 mr-2" />
                  Download SPLICE
                </Link>
              </Button>
              <Button variant="outline" size="lg" asChild>
                <Link href="/docs">View Documentation</Link>
              </Button>
            </motion.div>

            {/* Help text */}
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, delay: 0.8 }}
              className="mt-8 text-sm text-muted-foreground"
            >
              Need help getting started?{" "}
              <a
                href="mailto:support@splice.app"
                className="text-primary hover:underline"
              >
                Contact support
              </a>{" "}
              or check our{" "}
              <Link href="/docs/quickstart" className="text-primary hover:underline">
                quick start guide
              </Link>
              .
            </motion.p>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default function SuccessPage() {
  return (
    <Suspense
      fallback={
        <div className="min-h-screen flex items-center justify-center">
          <Loader2 className="w-8 h-8 animate-spin text-primary" />
        </div>
      }
    >
      <SuccessContent />
    </Suspense>
  );
}

