@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
  --color-sidebar-ring: var(--sidebar-ring);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar: var(--sidebar);
  --color-chart-5: var(--chart-5);
  --color-chart-4: var(--chart-4);
  --color-chart-3: var(--chart-3);
  --color-chart-2: var(--chart-2);
  --color-chart-1: var(--chart-1);
  --color-ring: var(--ring);
  --color-input: var(--input);
  --color-border: var(--border);
  --color-destructive: var(--destructive);
  --color-accent-foreground: var(--accent-foreground);
  --color-accent: var(--accent);
  --color-muted-foreground: var(--muted-foreground);
  --color-muted: var(--muted);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-secondary: var(--secondary);
  --color-primary-foreground: var(--primary-foreground);
  --color-primary: var(--primary);
  --color-popover-foreground: var(--popover-foreground);
  --color-popover: var(--popover);
  --color-card-foreground: var(--card-foreground);
  --color-card: var(--card);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --radius-2xl: calc(var(--radius) + 8px);
  --radius-3xl: calc(var(--radius) + 12px);
  --radius-4xl: calc(var(--radius) + 16px);
}

:root {
  --radius: 0.625rem;
  --background: oklch(0.98 0.005 200);
  --foreground: oklch(0.15 0.02 200);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.15 0.02 200);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.15 0.02 200);
  --primary: oklch(0.7 0.18 195);
  --primary-foreground: oklch(0.12 0.02 200);
  --secondary: oklch(0.96 0.01 200);
  --secondary-foreground: oklch(0.25 0.05 200);
  --muted: oklch(0.95 0.01 200);
  --muted-foreground: oklch(0.5 0.02 200);
  --accent: oklch(0.92 0.05 200);
  --accent-foreground: oklch(0.25 0.05 200);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.02 200);
  --input: oklch(0.9 0.02 200);
  --ring: oklch(0.7 0.18 195);
  --chart-1: oklch(0.7 0.18 195);
  --chart-2: oklch(0.65 0.15 210);
  --chart-3: oklch(0.6 0.12 220);
  --chart-4: oklch(0.7 0.15 180);
  --chart-5: oklch(0.75 0.18 170);
  --sidebar: oklch(0.98 0.005 200);
  --sidebar-foreground: oklch(0.15 0.02 200);
  --sidebar-primary: oklch(0.7 0.18 195);
  --sidebar-primary-foreground: oklch(0.12 0.02 200);
  --sidebar-accent: oklch(0.92 0.05 200);
  --sidebar-accent-foreground: oklch(0.25 0.05 200);
  --sidebar-border: oklch(0.9 0.02 200);
  --sidebar-ring: oklch(0.7 0.18 195);
}

.dark {
  --background: oklch(0.08 0.02 220);
  --foreground: oklch(0.95 0.01 200);
  --card: oklch(0.12 0.025 220);
  --card-foreground: oklch(0.95 0.01 200);
  --popover: oklch(0.12 0.025 220);
  --popover-foreground: oklch(0.95 0.01 200);
  --primary: oklch(0.75 0.18 195);
  --primary-foreground: oklch(0.08 0.02 220);
  --secondary: oklch(0.18 0.03 220);
  --secondary-foreground: oklch(0.95 0.01 200);
  --muted: oklch(0.18 0.03 220);
  --muted-foreground: oklch(0.65 0.02 200);
  --accent: oklch(0.2 0.05 220);
  --accent-foreground: oklch(0.95 0.01 200);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(0.2 0.03 220);
  --input: oklch(0.2 0.03 220);
  --ring: oklch(0.75 0.18 195);
  --chart-1: oklch(0.75 0.18 195);
  --chart-2: oklch(0.7 0.15 210);
  --chart-3: oklch(0.65 0.12 220);
  --chart-4: oklch(0.75 0.15 180);
  --chart-5: oklch(0.8 0.18 170);
  --sidebar: oklch(0.12 0.025 220);
  --sidebar-foreground: oklch(0.95 0.01 200);
  --sidebar-primary: oklch(0.75 0.18 195);
  --sidebar-primary-foreground: oklch(0.08 0.02 220);
  --sidebar-accent: oklch(0.2 0.05 220);
  --sidebar-accent-foreground: oklch(0.95 0.01 200);
  --sidebar-border: oklch(0.2 0.03 220);
  --sidebar-ring: oklch(0.75 0.18 195);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }
}

/* Custom animations */
@keyframes gradient-x {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px rgba(0, 212, 255, 0.3);
  }
  50% {
    box-shadow: 0 0 40px rgba(0, 212, 255, 0.6);
  }
}

@keyframes neon-flicker {
  0%, 19%, 21%, 23%, 25%, 54%, 56%, 100% {
    text-shadow:
      0 0 5px #00D4FF,
      0 0 10px #00D4FF,
      0 0 20px #00D4FF,
      0 0 40px #00A8CC;
  }
  20%, 24%, 55% {
    text-shadow: none;
  }
}

.animate-gradient-x {
  background-size: 200% 200%;
  animation: gradient-x 15s ease infinite;
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

/* Gradient text - Cyan theme */
.gradient-text {
  background: linear-gradient(135deg, #00D4FF 0%, #0099CC 50%, #00D4FF 100%);
  background-size: 200% auto;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradient-x 5s ease infinite;
}

/* Neon glow effect for logo */
.neon-glow {
  filter: drop-shadow(0 0 10px rgba(0, 212, 255, 0.5)) drop-shadow(0 0 20px rgba(0, 212, 255, 0.3));
}

/* Hero gradient background - Dark with cyan accents */
.hero-gradient {
  background: linear-gradient(135deg,
    rgba(0, 212, 255, 0.05) 0%,
    rgba(0, 168, 204, 0.02) 50%,
    rgba(0, 212, 255, 0.05) 100%
  );
}

.dark .hero-gradient {
  background:
    radial-gradient(ellipse at 20% 20%, rgba(0, 212, 255, 0.15) 0%, transparent 50%),
    radial-gradient(ellipse at 80% 80%, rgba(0, 168, 204, 0.1) 0%, transparent 50%),
    linear-gradient(180deg, oklch(0.08 0.02 220) 0%, oklch(0.06 0.015 220) 100%);
}

/* Feature card hover effect */
.feature-card {
  transition: all 0.3s ease;
}

.feature-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 40px rgba(0, 212, 255, 0.15);
}

/* Glass morphism - always dark for consistent header */
.glass {
  background: rgba(15, 23, 42, 0.9);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
}

.dark .glass {
  background: rgba(15, 23, 42, 0.9);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
}

/* Smooth scroll */
html {
  scroll-behavior: smooth;
}

/* Hide scrollbar but keep functionality */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Button glow effect */
.btn-glow {
  box-shadow: 0 0 20px rgba(0, 212, 255, 0.4);
  transition: all 0.3s ease;
}

.btn-glow:hover {
  box-shadow: 0 0 30px rgba(0, 212, 255, 0.6);
}

/* Grid pattern background */
.grid-pattern {
  background-image:
    linear-gradient(rgba(0, 212, 255, 0.03) 1px, transparent 1px),
    linear-gradient(90deg, rgba(0, 212, 255, 0.03) 1px, transparent 1px);
  background-size: 50px 50px;
}

