"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Scissors,
    Music,
    MousePointer2,
    Zap,
    CheckCircle2,
    Clock
} from "lucide-react";

// Waveforms for the "Raw" state (gappy)
const RAW_WAVEFORMS = Array.from({ length: 40 }).map((_, i) => ({
    height: Math.max(20, Math.random() * 100),
    isSilence: i % 5 === 0 || i % 6 === 0 || i % 13 === 0, // Random gaps
}));

// Waveforms for the "Cut" state (condensed, no gaps)
const CUT_WAVEFORMS = RAW_WAVEFORMS.filter(w => !w.isSilence).map(w => ({
    ...w,
    height: w.height
}));

// Pre-generated music waveform heights (computed once at module load)
const MUSIC_WAVEFORM_HEIGHTS = Array.from({ length: 30 }).map(() => Math.random() * 100);

export function ProductDemo() {
    const [step, setStep] = useState<"intro" | "scanning" | "cutting" | "music" | "done">("intro");

    useEffect(() => {
        let mounted = true;
        const timeouts: NodeJS.Timeout[] = [];

        const delay = (ms: number) => new Promise<void>(resolve => {
            const id = setTimeout(() => {
                if (mounted) resolve();
            }, ms);
            timeouts.push(id);
        });

        const sequence = async () => {
            // 1. Intro (Scanning raw footage)
            await delay(1000);
            if (mounted) setStep("scanning");

            // 2. Cursor moves to "Remove Silence" and clicks (handled by animations)
            // Wait for scan to finish
            await delay(2000);

