file.content:
"use client";

import { motion } from "framer-motion";
import { Download, Upload, Sparkles, Play } from "lucide-react";

const steps = [
  {
    number: "01",
    icon: Download,
    title: "Install Plugin",
    description: "Download SPLICE and install it in Premiere Pro. Takes under 2 minutes.",
  },
  {
    number: "02",
    icon: Upload,
    title: "Open Your Project",
    description: "Load any video or audio file. Works with podcasts, vlogs, interviews, and more.",
  },
  {
    number: "03",
    icon: Sparkles,
    title: "Click Analyze",
    description: "AI detects silences, takes, and filler words. Preview cuts before applying.",
  },
  {
    number: "04",
    icon: Play,
    title: "Export & Share",
    description: "Your rough cut is ready. Fine-tune if needed, then export to any platform.",
  },
];

const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.15,
    },
  },
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 },
};

export function HowItWorks() {
  return (
    <section className="py-20 bg-[#0a0f1a] dark relative overflow-hidden">
      {/* Background decoration */}
      <div className="absolute inset-0 grid-pattern opacity-20" />

      <div className="container mx-auto px-4 relative z-10">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4 text-white">
            How It Works
          </h2>
          <p className="text-lg text-gray-400 max-w-xl mx-auto">
            From install to export in 4 simple steps. No learning curve required.
          </p>
        </motion.div>

        {/* Steps */}
        <motion.div
          variants={container}
          initial="hidden"
          whileInView="show"
          viewport={{ once: true }}
          className="grid md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-6xl mx-auto"
        >
          {steps.map((step, index) => (
            <motion.div
              key={step.number}
              variants={item}
              className="relative"
            >
              {/* Connector line (hidden on mobile and last item) */}
              {index < steps.length - 1 && (
                <div className="hidden lg:block absolute top-12 left-[60%] w-[80%] h-[2px] bg-gradient-to-r from-[#00D4FF]/30 to-transparent" />
              )}

              <div className="text-center">
                {/* Step number + icon */}
                <div className="relative inline-flex items-center justify-center mb-6">
                  <div className="w-24 h-24 rounded-2xl bg-[#00D4FF]/10 border border-[#00D4FF]/20 flex items-center justify-center">
                    <step.icon className="w-10 h-10 text-[#00D4FF]" />
                  </div>
                  <span className="absolute -top-2 -right-2 w-8 h-8 rounded-full bg-[#00D4FF] text-black text-sm font-bold flex items-center justify-center">
                    {step.number.replace("0", "")}
                  </span>
                </div>

                {/* Content */}
                <h3 className="text-xl font-semibold mb-2 text-white">
                  {step.title}
                </h3>
                <p className="text-gray-400 text-sm leading-relaxed">
                  {step.description}
                </p>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Bottom CTA */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.4 }}
          className="text-center mt-12"
        >
          <p className="text-gray-500 text-sm">
            Average setup time: <span className="text-[#00D4FF] font-semibold">2 minutes</span> ·
            Average time saved per project: <span className="text-[#00D4FF] font-semibold">3+ hours</span>
          </p>
        </motion.div>
      </div>
    </section>
  );
}

