## Code Actions & Refactoring

### Complete Code Action Provider Example

```typescript
// src/codeActionProvider.ts
import * as vscode from 'vscode';
import * as ts from 'typescript';

export class RefactoringProvider implements vscode.CodeActionProvider {
  static readonly providedCodeActionKinds = [
    vscode.CodeActionKind.Refactor,
    vscode.CodeActionKind.RefactorExtract,
    vscode.CodeActionKind.QuickFix,
  ];

  async provideCodeActions(
    document: vscode.TextDocument,
    range: vscode.Range,
    context: vscode.CodeActionContext
  ): Promise<(vscode.CodeAction | vscode.Command)[]> {
    const actions: vscode.CodeAction[] = [];

    // Parse document
    const sourceFile = ts.createSourceFile(
      document.uri.fsPath,
      document.getText(),
      ts.ScriptTarget.Latest,
      true
    );

    // Check for extract variable refactoring
    const selectedText = document.getText(range);
    if (selectedText && !selectedText.includes('\n')) {
      const extractAction = new vscode.CodeAction(
        'Extract to variable',
        vscode.CodeActionKind.RefactorExtract
      );

      extractAction.command = {
        command: 'extension.extractVariable',
        title: 'Extract to variable',
        arguments: [document.uri, range],
      };

      actions.push(extractAction);
    }

    // Check for inline variable refactoring
    if (isVariableDeclaration(sourceFile, range)) {
      const inlineAction = new vscode.CodeAction(
        'Inline variable',
        vscode.CodeActionKind.Refactor
      );

      inlineAction.command = {
        command: 'extension.inlineVariable',
        title: 'Inline variable',
        arguments: [document.uri, range],
      };

      inlineAction.isPreferred = true;
      actions.push(inlineAction);
    }

    // Add quick fixes for diagnostics
    for (const diagnostic of context.diagnostics) {
      if (diagnostic.message.includes('var')) {
        const fixAction = new vscode.CodeAction(
          'Replace with let/const',
          vscode.CodeActionKind.QuickFix
        );

        fixAction.diagnostics = [diagnostic];
        fixAction.edit = new vscode.WorkspaceEdit();
        fixAction.edit.replace(
          document.uri,
          diagnostic.range,
          'let'
        );

        actions.push(fixAction);
      }
    }

    return actions;
  }
}

function isVariableDeclaration(
  sourceFile: ts.SourceFile,
  range: vscode.Range
): boolean {
  // Simplified check - would need proper AST traversal
  return false;
}

// Register provider
export function registerRefactoringProvider(context: vscode.ExtensionContext) {
  const provider = new RefactoringProvider();

  context.subscriptions.push(
    vscode.languages.registerCodeActionProvider(
      { scheme: 'file', language: 'javascript' },
      provider,
      {
        providedCodeActionKinds:
          RefactoringProvider.providedCodeActionKinds,
      }
    )
  );

  // Register extract variable command
  context.subscriptions.push(
    vscode.commands.registerCommand(
      'extension.extractVariable',
      async (uri: vscode.Uri, range: vscode.Range) => {
        const document = await vscode.workspace.openTextDocument(uri);
        const selectedText = document.getText(range);

        // Prompt for variable name
        const varName = await vscode.window.showInputBox({
          prompt: 'Variable name:',
          placeHolder: 'myVar',
        });

        if (!varName) return;

        // Get insertion point (find line above selection)
        const insertLine = range.start.line;
        const insertPos = new vscode.Position(insertLine, 0);

        // Create edit
        const edit = new vscode.WorkspaceEdit();
        edit.insert(
          uri,
          insertPos,
          `const ${varName} = ${selectedText};\n`
        );
        edit.delete(uri, range);

        // Apply edits
        await vscode.workspace.applyEdit(edit);
      }
    )
  );
}
